/* Main Settings */

#navigator-toolbox
{border: none !important;}

#navigator-toolbox::after
{height: 0 !important;}


/* Navigation Bar Settings */

#navigator-toolbox #nav-bar
{background: -moz-linear-gradient(top,#fff,rgb(190,190,190)) !important;
border: .5px solid rgb(136,134,129) !important;
border-bottom: none !important;
padding-left: 0 !important;
padding-right: 0 !important;}

#navigator-toolbox[tabsontop="false"] #nav-bar
{margin-top: 4px !important;
border-radius: 4px 4px 0 0 !important;}

#navigator-toolbox .toolbarbutton-text
{color: #000000 !important;
text-shadow: none !important;}




/* Menubar Settings */


#toolbar-menubar[autohide="false"] #main-menubar
{background-color: transparent !important;}

#navigator-toolbox #toolbar-menubar[autohide="false"] .menubar-text
{color: #000000 !important;
text-shadow: 0 1px 3px rgb(210,220,235), 0 -1px 3px rgb(210,220,235) !important;}




/* Tab Bar Settings */

#TabsToolbar[tabsontop="true"]
{background: transparent !important;
padding-left: 2px !important;
margin-top: 4px !important;
border: none !important;}


#TabsToolbar[tabsontop="false"]
{background: #363636 !important;
margin-left: -4px !important;
border: none !important;}




/* Tab Settings */


.tabs-newtab-button
{background: -moz-radial-gradient(rgba(255,255,255,0.7) 20%, rgba(200,200,200,0), rgba(200,200,200,0)) !important;
border: none !important;}


.tabbrowser-tab:not([selected="true"])
{background: rgb(150,150,150) !important;
border: none !important;
border-radius: 4px 4px 0 0 !important;
margin-left: 2px !important;
margin-right: 2px !important;}

#navigator-toolbox[tabsontop="false"] .tabbrowser-tab:not([selected="true"])
{border-left: .5px solid rgb(136,134,129) !important;
border-right: .5px solid rgb(136,134,129) !important;
border-radius: 0 !important;}

.tabbrowser-tab:not([selected="true"]):hover
{background: rgb(170,170,170) !important;}

.tabbrowser-tab:not([selected="true"]) .tab-text
{color: #000000 !important;
text-align: center !important;
font-size: 8pt !important;
font-family: segoe ui !important;
font-style: italic !important;
text-shadow: none !important;}


.tabbrowser-tab[selected="true"]
{background: -moz-linear-gradient(-50deg,#fff,rgb(170,170,170)) !important;
border: none !important;
border-radius: 4px 4px 0 0 !important;
margin-left: 2px !important;
margin-right: 2px !important;}

#navigator-toolbox[tabsontop="false"] .tabbrowser-tab[selected="true"]
{border-left: .5px solid rgb(136,134,129) !important;
border-right: .5px solid rgb(136,134,129) !important;
border-radius: 0 !important;}


.tabbrowser-tab[selected="true"] .tab-text
{color: #000000 !important;
font-size: 9pt !important;
font-family: segoe ui !important;
text-align: center !important;
text-shadow: none !important;
margin-right: 3px !important;}

.tab-icon-image {opacity: 0.7 !important;}


/* Appmenu Button */
#appmenu-button dropmarker {display: none !important;}

#appmenu-button .button-text {color: rgb(10,10,10) !important;
font-weight: normal !important;
text-shadow: none !important;}

#main-window:not([privatebrowsingmode]) #appmenu-button
{background: -moz-linear-gradient(left, #fff, rgba(200,200,200,0))!important;
border: none !important;
box-shadow: inset -1px 0 1px rgba(72,73,77,0.7) !important;
border-radius: 0 !important;}

#main-window[privatebrowsingmode] #appmenu-button
{background: -moz-linear-gradient(left, #fff, rgba(172,151,176,0.5))!important;
border: none !important;
box-shadow: inset -1px 0 1px rgba(72,73,77,0.7) !important;
border-radius: 0 !important;}

#main-window[privatebrowsingmode] #appmenu-button .button-box .box-inherit:before 
{color: rgb(10,10,10) !important;
font-weight: normal !important;
text-shadow: none !important;}


/* Bookmarks Bar Settings */

#navigator-toolbox #PersonalToolbar
{background: rgb(220,220,220) !important;
border: .5px solid rgb(136,134,129) !important;
box-shadow: inset 0 0 3px rgba(102,103,107,0.7) !important;}

#navigator-toolbox[tabsontop="false"] #PersonalToolbar
{margin-top: 0 !important;
border-radius: 0 !important;}


#navigator-toolbox #PersonalToolbar .toolbarbutton-text
{color: #000000 !important;
font-size: 9pt !important;}

#PersonalToolbar .toolbarbutton-icon
{opacity: 0.8 !important;}



/* Bookmarks SideBar Settings */

sidebarheader
{-moz-appearance: none !important;
background: -moz-linear-gradient(rgb(170,170,170), rgb(215,215,215), rgb(180,180,180)) !important;
color: #000000 !important;
font-size: 12pt !important;
text-align: center !important;
border: none !important;}

#sidebar-splitter {background: rgba(72,73,77,0.7) !important;
min-width: .5px !important;
max-width: .5px !important;
border: none !important;}

#history-panel > hbox,
#bookmarksPanel > hbox
{-moz-appearance: none !important;
background-color: rgb(200,200,200) !important;
color: #000000 !important;
border: none !important;}

#bookmarks-view,
#historyTree 
{-moz-appearance: none !important;
border: none !important;
color: #000000 !important;
background:-moz-linear-gradient(top,#fff,#999) !important;}





/* Toolbar Button Settings */

.toolbarbutton-1 {
background: transparent !important;
border: none !important;
box-shadow: none !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-button
{background: transparent !important;
border: none !important;
box-shadow: none !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker
{background: none !important;
border: none !important;
box-shadow: none !important;}


#TabsToolbar .toolbarbutton-1
{background: transparent !important;
border: none !important;
box-shadow: none !important;}

#back-button:not([disabled="true"]):hover {background: transparent !important;
box-shadow: none !important;}




/* URL and SearchBox Settings */

#searchFilter,
#urlbar,
.searchbar-textbox 
{background-color: transparent !important;
color: #000000 !important;
border: .5px solid rgba(72,73,77,0.7) !important;
border-radius: 4px !important;
box-shadow: none !important;}


#identity-box {border-radius: 4px !important;
opacity: 0.7 !important;}

#searchbar .searchbar-engine-image
{border-radius: 4px !important;
opacity: 0.7 !important;}




/* ScrollBar Settings */

scrollbar[orient="vertical"] 
{-moz-appearance: none !important;
background: #363636 !important;}

scrollbar[orient="horizontal"]
{ -moz-appearance: none ! important;
background: #363636 !important;}

scrollbar thumb[orient="vertical"] 
{ -moz-appearance: none ! important;
background: -moz-linear-gradient(left,#fff,rgb(190,190,190)) !important;
border: .5px solid #363636 !important;
border-radius: 2px !important;
min-height: 15px !important;}

scrollbar thumb[orient="horizontal"]
{ -moz-appearance: none ! important;
background: -moz-linear-gradient(top,#fff,rgb(190,190,190)) !important;
border: .5px solid #363636 !important;
border-radius: 2px !important;
min-width: 15px !important;}


scrollbar scrollbarbutton, scrollbar gripper 
{ -moz-appearance: none ! important;
background: -moz-linear-gradient(left,#fff,rgb(190,190,190)) !important;
border: .5px solid #363636 !important;
border-radius: 2px !important;}



/* Bottom Browser settings for Addon Bar and Find Bar */

#browser-bottombox
{ -moz-appearance: none !important;
background: transparent !important; 
border: none !important;
box-shadow: none !important;}


#addon-bar
{-moz-appearance: none !important;
color: #000000 !important;
border-top: none !important;
border-bottom: none !important;
border-left: .5px solid rgb(136,134,129) !important;
border-right: .5px solid rgb(136,134,129) !important;
border-radius: 0 !important;
background: -moz-linear-gradient(rgb(172,168,160), rgb(212,208,200), rgb(232,228,220)) !important;}


#FindToolbar {
color: #000000 !important;
border: .5px solid rgba(72,73,77,0.7) !important;
background: rgb(220,220,220) !important;}

#FindToolbar .toolbarbutton-text {color: #000000 !important;}

.statuspanel-label {
background: rgb(190,190,190) !important;
border: none !important;
color: #141414 !important;
border-radius: 0 !important;
}

tooltip {
-moz-appearance: none !important;
background: rgb(190,190,190) !important;
border: none !important;
color: #141414 !important;
border-radius: 0 !important;
}
