css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* Compacting Code */

#main-window[sizemode="normal"] #navigator-toolbox #toolbar-menubar[autohide="true"]
{position: fixed !important;
top:120px !important;
left:0px !important;
background: #000000 !important;}


#main-window:not([sizemode="fullscreen"]) #navigator-toolbox[tabsontop="true"] #toolbar-menubar[autohide="false"]
{position: fixed !important;
right:0 !important;}

#main-window:not([sizemode="fullscreen"]) #navigator-toolbox:not([customizing])[tabsontop="false"] #toolbar-menubar[autohide="false"]
{min-height: 3px !important;
max-height: 3px !important;
opacity: 0 !important;}

#main-window:not([sizemode="fullscreen"]) #navigator-toolbox:not([customizing])[tabsontop="false"] #toolbar-menubar[autohide="false"]:hover
{min-height: 0 !important;
max-height: 0 !important;
opacity: 1.0 !important;}


#navigator-toolbox #toolbar-menubar .menubar-text
{font-family: corbel !important;
font-size: 8pt !important;}



#main-window[sizemode="normal"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-right: 24em !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-right: 25em !important;
padding-left: 2px !important;}



#main-window[sizemode="normal"] #toolbar-menubar[autohide="true"] ~ #TabsToolbar[tabsontop="true"]
{margin-top: -15px !important;
margin-left: 80px !important;
margin-right: 11em !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: 0 !important;}

#main-window[sizemode="normal"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: -4px !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: -4px !important;}


#appmenu-button dropmarker {display: none !important;}


#main-window[sizemode="normal"][tabsontop="true"] #appmenu-button
{margin-top: 10px !important;}



#main-window[privatebrowsingmode] #appmenu-button .button-box .box-inherit:after 
{content: "Private";}

#main-window[privatebrowsingmode] #appmenu-button .button-box .box-inherit .button-text 
{display: none;} 


/* Main Settings */

#navigator-toolbox
{border: none !important;}

#navigator-toolbox::after
{height: 0 !important;}


/* Navigation Bar Settings */

#navigator-toolbox #nav-bar
{background: transparent !important;
border: none !important;
padding-left: 0 !important;
padding-right: 0 !important;}

#navigator-toolbox .toolbarbutton-text
{color: #000000 !important;
text-shadow: 0 1px 2px rgb(160,165,170), 0 1px 3px rgb(130,135,140), 0 -1px 2px rgb(160,165,170), 0 -1px 3px rgb(130,135,140) !important;}




/* Menubar Settings */


#toolbar-menubar[autohide="false"] #main-menubar
{background-color: transparent !important;}

#navigator-toolbox #toolbar-menubar[autohide="false"] .menubar-text
{color: #000000 !important;
text-shadow: 0 1px 3px rgb(210,220,235), 0 -1px 3px rgb(210,220,235) !important;}




/* Tab Bar Settings */




#main-window:not([sizemode="fullscreen"]) #TabsToolbar[tabsontop="false"]
{background: transparent !important;
padding-left: 0 !important;
border: none !important;}





/* Tab Settings */


.tabs-newtab-button
{background: transparent !important;
border: none !important;}


.tabbrowser-tab:not([selected="true"])
{background: rgba(161,170,175,0.6) !important;
border: .5px solid rgba(21,30,35,0.5) !important;
border-radius: 0 !important;}

.tabbrowser-tab:not([selected="true"]):hover
{background: rgba(181,190,195,0.6) !important;}

.tabbrowser-tab:not([selected="true"]) .tab-text
{color: #000000 !important;
text-align: center !important;
font-size: 8pt !important;
font-family: segoe ui !important;
font-style: italic !important;
text-shadow: none !important;}


.tabbrowser-tab[selected="true"]
{background: rgba(221,230,235,0.6) !important;
border: .5px solid rgba(21,30,35,0.5) !important;
border-radius: 0 !important;}


.tabbrowser-tab[selected="true"] .tab-text
{color: #000000 !important;
font-size: 9pt !important;
font-family: segoe ui !important;
text-align: center !important;
text-shadow: none !important;
margin-right: 3px !important;}

.tab-icon-image {opacity: 0.7 !important;}



/* App Button Settings */

#appmenu-button .button-text {color: #000000 !important;
font-weight: normal !important;
text-shadow: none !important;}

#appmenu-button
{background: rgba(161,170,175,0.6) !important;
border: .5px solid rgba(21,30,35,0.5) !important;
box-shadow: none !important;
border-radius: 0 !important;}

#appmenu-button:hover
{background: rgba(191,200,205,0.6) !important;}

#main-window[privatebrowsingmode] #appmenu-button .button-box .box-inherit:before 
{color: #000000 !important;
font-weight: normal !important;
text-shadow: none !important;}




/* Bookmarks Bar Settings */

#navigator-toolbox #PersonalToolbar
{background: rgba(191,200,205,0.6) !important;
border: .5px solid rgba(21,30,35,0.5) !important;}

#navigator-toolbox[tabsontop="false"] #PersonalToolbar
{background-color: rgba(191,200,205,0.6) !important;
margin-top: 0 !important;
border-radius: 0 !important;}


#navigator-toolbox #PersonalToolbar .toolbarbutton-text
{color: #000000 !important;
font-size: 9pt !important;}

#PersonalToolbar .toolbarbutton-icon
{opacity: 0.8 !important;}



/* Bookmarks SideBar Settings */

sidebarheader
{-moz-appearance: none !important;
background: rgba(171,180,185,0.6) !important;
color: #000000 !important;
font-size: 12pt !important;
text-align: center !important;
border: none !important;}

#sidebar-splitter {background: rgba(21,30,35,0.5) !important;
min-width: .5px !important;
max-width: .5px !important;
border: none !important;}

#history-panel > hbox,
#bookmarksPanel > hbox
{-moz-appearance: none !important;
background-color: #202020 !important;
color: #9d9d9d !important;
border: none !important;}

#bookmarks-view,
#historyTree 
{-moz-appearance: none !important;
border: none !important;
color: #000000 !important;
background-color: rgb(131,140,145) !important;}








/* URL and SearchBox Settings */

#searchFilter,
#urlbar,
.searchbar-textbox 
{background-color: rgba(191,200,205,0.6) !important;
color: #000000 !important;
border: .5px solid rgba(21,30,35,0.5) !important;
border-radius: 0 !important;
box-shadow: none !important;}



#urlbar:hover,
.searchbar-textbox:hover
{background-color: rgba(211,220,225,0.6) !important;}

#identity-box {border: none !important;
border-radius: 0 !important;
box-shadow: inset 0 0 .5px .5px rgba(21,30,35,0.5) !important;
opacity: 0.6 !important;}



#searchbar .searchbar-engine-image
{display: none !important;}




/* ScrollBar Settings */

scrollbar[orient="vertical"] 
{-moz-appearance: none !important;
background: #141414 !important;}

scrollbar[orient="horizontal"]
{ -moz-appearance: none ! important;
background: #141414 !important;}

scrollbar thumb[orient="vertical"] 
{ -moz-appearance: none ! important;
background: rgb(151,160,165) !important;
border: .5px solid #141414 !important;
border-radius: 0 !important;
min-height: 15px !important;}

scrollbar thumb[orient="vertical"]:hover 
{background: rgb(181,190,195) !important;}

scrollbar thumb[orient="vertical"]:active
{background: rgb(181,190,195) !important;}


scrollbar thumb[orient="horizontal"]
{ -moz-appearance: none ! important;
background: rgb(151,160,165) !important;
border: .5px solid #141414 !important;
border-radius: 0 !important;
min-width: 15px !important;}


scrollbar thumb[orient="horizontal"]:hover
{background: rgb(181,190,195) !important;}

scrollbar thumb[orient="horizontal"]:active
{background: rgb(181,190,195) !important;}


scrollbar scrollbarbutton, scrollbar gripper 
{ -moz-appearance: none ! important;
min-height: 12px !important;
max-height: 12px !important;
min-width: 12px !important;
max-width: 12px !important;
background: rgb(161,170,175) !important;
border: .5px solid #141414 !important;
border-radius: 0 !important;}





/* Bottom Browser settings for Addon Bar and Find Bar */

#browser-bottombox
{ -moz-appearance: none !important;
background: transparent !important; 
border: none !important;
box-shadow: none !important;}


#addon-bar
{-moz-appearance: none !important;
border: .5px solid rgba(21,30,35,0.5) !important;
border-radius: 0 !important;
background: rgba(161,170,175,0.6) !important;}

#addon-bar .toolbarbutton-text
{color: #000000 !important;
text-shadow: 0 1px 2px rgb(160,165,170), 0 1px 3px rgb(130,135,140), 0 -1px 2px rgb(160,165,170), 0 -1px 3px rgb(130,135,140) !important;} 

#FindToolbar {
color: #000000 !important;
border: .5px solid rgba(21,30,35,0.5) !important;
background: rgba(161,170,175,0.6) !important;}

#FindToolbar .toolbarbutton-text {color: #000000 !important;}

.statuspanel-label {
   background: rgb(169,175,178) !important;
border: .5px solid rgb(21,30,35) !important;
color: #000000 !important;
}

tooltip {
-moz-appearance: none !important;
background: rgb(169,175,178) !important;
border: .5px solid rgb(21,30,35) !important;
color: #000000 !important;
}
