/* myanimelist.net -- suigintou night theme ANIMELIST by viilo [0.3]
 * vim:et:
 *
 * This style is for myanimelist.net custom anime/manga lists.
 *
 * Usage:
 * a) Copy/paste anything within [MAL-style section] (below)
 *    in 'Advanced Style CSS Editor' on the MAL-site.
 * b) Using via stylish will override any other MAL-users custom styles for you.
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
    url-prefix("http://myanimelist.net/animelist"),
    url-prefix("http://myanimelist.net/mangalist")
{

/* [MAL-style section] BEGIN */
body {
    background-image: url( "http://savepic.net/8450879.jpg" ) !important;
    background-attachment:  fixed  !important;
    background-repeat:      no-repeat !important;
    background-position:    center bottom !important;

    color:                  #CCC !important;
    font-size:              12.00px !important;
    font-family:            Ubuntu, Verdana, Arial !important;
    background-color:       #000 !important;
}

/* Determines the positioning of your list */
#list_surround
{
    margin-left:            40px !important;
    width:                  920px !important;
    padding-left:           4px !important;
    padding-right:          4px !important;
    background:             rgb( 0, 0, 0 ) !important;
    background:             rgba( 0, 0, 0, 0.5 ) !important;
}

/* All links on your list */
a {
    color:                  #768 !important;
    text-decoration:        none !important;
}

a:visited {
    color:                  #768 !important;
    text-decoration:        none !important;
}

a:hover {
    color:                  #A9B !important;
    text-decoration:        underline !important;
}

/* Alternating row color 1 */
.td1 {
    color:                  #CCC !important;
    border-width:           0px 1px 1px 0px !important;
    border-style:           solid !important;
    border-color:           #000000 !important;
    padding:                2px !important;
    background-color:       rgb( 33, 33, 33 ) !important;
    background-color:       rgba( 33, 33, 33, 0.8 ) !important;
}

/* Alternating row color 2 */
.td2 {
    color:                  #CCC !important;
    border-width:           0px 1px 1px 0px !important;
    border-style:           solid !important;
    border-color:           #000000 !important;
    padding:                2px !important;
    background-color:       rgb( 55, 55, 55 ) !important;
    background-color:       rgba( 55, 55, 55, 0.8 ) !important;
}

/* This represents the "Anime Title", "Score", "# Eps" columns */
.table_header {
    color:                  #CCC !important;
    border-style:           none !important;
    border-color:           #000 !important;
    border-bottom:          1px solid #555 !important;
    background-color:       #000000 !important;
    padding:                2px !important;
}

/* headerLink represents the color of the links inside the_header */
.table_headerLink {
}

.table_headerLink:visited {
}

.table_headerLink:hover {
}

/* Controls the select form decoration (the drop down select box) */
.form {
    border-width:           1px 1px 1px 1px !important;
    border-color:           #000000 !important;
    border-style:           solid !important;
    color:                  #000000 !important;
    padding:                2px !important;
    font-size:              11.00px !important;
    font-family:            Verdana, Arial !important;
}

/* Which 'status' up top is selected? */
.status_selected {
    color:                  #FFFFFF !important;
    border-width:           0px 1px 1px 0px !important;
    border-style:           solid !important;
    border-color:           #000000 !important;
    padding:                2px !important;
    font-weight:            bold !important;
    background-color:       rgb( 55, 55, 55 )  !important;
    background-color:       rgba( 55, 55, 55, 0.8 )  !important;
}

.status_not_selected {
    color:                  #FFFFFF !important;
    border-width:           0px 1px 1px 0px !important;
    border-style:           solid !important;
    border-color:           #000000 !important;
    padding:                2px !important;
    font-weight:            normal !important;
    background-color:       rgb( 33, 33, 33 )  !important;
    background-color:       rgba( 33, 33, 33, 0.8 )  !important;
}

/* Header classes for Currently Watching, Completed, Dropped, etc... */
.header_cw {
}

.header_completed {
}

.header_onhold {
}

.header_dropped {
}

.header_ptw {
}

.header_title {
    font-size:              14px !important;
    font-weight:            bold !important;
}

.category_totals {
}

#grand_totals {
    text-align:             center !important;
}

/* header_al is thesurrounding "User's Anime List" at the top */
.header_al {
    font-weight:            bold !important;
    font-size:              16px !important;
}

/* header_al_links is thewith your "Profile" and "MyAnimeList home" links */
.header_al_links {
}

/* controls what styles you can give to all the anime titles in your list */
.animetitle {
    font-weight:            bold !important;
}

/* copyright contains the "Producted by Garrett Gyssler" text
 * DO NOT REMOVE OR HIDE THIS DIV
 * IF FOUND TO BE REMOVED, YOUR LIST WILL BE REMOVED TOO
 */
#copyright {
    padding-top:            6px !important;
    text-align:             center !important;
    margin:                 0 auto !important;
    width:                  900px !important;
    background:             rgb( 0,0,0 ) !important;
    background:             rgba( 0,0,0, 0.7 ) !important;
}

#copyright:after {
    content:                " Suigintou Night Theme by viilo" !important;
}
/* [MAL-style section] END */

/* Making fixed MAL top header */
table#mal_control_strip {
    position:               fixed !important;
    z-index:                10 !important;
}
#list_surround
{
    position:               relative !important;
    top:                    40px !important;
}

table#mal_control_strip td {
    background-color:       rgb( 22, 22, 22 ) !important;
    background-color:       rgba( 22, 22, 22, 0.6 ) !important;
}

/* [v0.3] */
#fancybox-outer {
    border: 2px solid #657 !important;
    background: #000 !important;
    position: fixed !important;
    top: 10% !important;
    width: 1000px !important;
    height: 80% !important;
}

#fancybox-inner {
    width: 980px !important;
    height: 96% !important;
}

}

@-moz-document
    url-prefix("http://myanimelist.net/editlist.php")
{

body[onload=""] {
    background-position: bottom left !important;
}

table#dialog {
    background: rgba( 0,0,0, 0.8 ) !important;
}

}
