css:
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http://tieba\\.baidu\\.com/(p/|f\\?ct=|f\\?kz=).*") { 
/*帖子正文和标题背景色*/
.l_thread_title, .p_postlist, .d_post_split, .d_comment_last {
    background-color: transparent !important;
}

/*帖子宽度*/
.p_postlist, .p_thread, .p_ding_down {
    width: 99% !important;
}

/*帖子正文和标题字体*/
.l_thread_title, .d_post_content {
    font-family: "Microsoft Yahei",sans-serif !important; 
}

/*隐藏签名图, 旧版徽章和分享按钮等*/
.d_sign_split, .d_sign_split + img, .badge, .hdl, #goTop, #bdshare, .d_forward_info, .p_ding_up,
img[src="http://hiphotos.baidu.com/matrox/pic/item/3bb2a319d6633f0c42a9adbc.jpg"] {
    display: none !important;
}

/*头像区的宽度*/
.d_author, .d_author_anonym {
    width: 82px !important;
}

/*头像区的高度*/
.d_avatar_frame { 
    height: 60px !important;
    padding: 0 0 !important;
}

/*头像的大小*/
.p_author_face > img {
    max-width: 60px !important;
    max-height: 60px !important;
}

/*提示信息的位置*/
#TipParentDiv {
    left: 85px !important;
}

/*等级徽章背景*/
.d_badge, .d_badge_lv, .d_ding_btn_default {
    background: none !important;
}

/*楼层数和正文的距离*/
.d_floor {
    margin-bottom: -10px !important;
}

/*图片大小*/
.BDE_Image, .d_content_img {
    max-width: 100% !important;}
}

@-moz-document domain("tieba.baidu.com") {
/*发帖框背景色*/
.tb-editor-wrapper, .edit_title_field, .s_btn_wr  {
  background: none !important;
}
}
