css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.178.com") {
/*
author tumuyan
date 2012 - 4
ver 0.1
*/
body {background:#fefee0 !important;}
}

@-moz-document url-prefix("http://www.178.com/mh") {
div[id*="nav"], .display_box dl dt div {display:none !important;}

div[id*="nav"] a[href="http://i.178.com/~sms.index.index"]
{position:fixed;left:0px;top:10px;display:inline !important;}

a[href="http://account.178.com/"]
{position:fixed;left:20px;top:0px;display:inline !important;}

.display_box{
position:fixed !important;bottom:-50px !important;left:auto !important;z-index:10 !important;
-o-transition: .3s ;
-webkit-transition: .3s;
-moz-transition: .3s;
}

.display_box:hover{
position:fixed !important;bottom:0px !important;z-index:20 !important;
-o-transition: .5s ease-out;
-webkit-transition: .5s ease-out;
-moz-transition: .5s ease-out;
}

.display_graybg
{position:fixed !important;right:0px !important;bottom:0px !important;width:auto !important;background:transparent !important;}
.display_graybg:hover
{z-index:20 !important;right:0px !important;bottom:0px !important;width:auto !important;
background:#fcfcfc !important;
-o-transition: .5s ease-in;
-webkit-transition: .5s ease-in;
-moz-transition: .5s ease-in;
}

.funcdiv{position:fixed !important;bottom:30px !important;width:860px !important;overflow:hidden !important;right:0px !important;background:transparent !important;}

.funcdiv:hover{position:fixed !important;bottom:30px !important;background:#fcfcfc !important;
-o-transition: .5s ease-out;
-webkit-transition: .5s ease-out;
-moz-transition: .5s ease-out;
}

span.red1_font12,
.box_text,.box_text ~ div 
,.change_btn
,ul[style="height:19px;line-height:19px;float:right;"]
{display:none !important;}

img.bigimgborder{

top:0px !important;position:absolute !important;margin:auto !important;left:0px !important;
}

.interactive-opinion{

width:100% !important;
position:fixed !important;top:-90px !important;margin:auto;border:0px !important;left:0px !important}

.interactive-opinion:hover{position:fixed !important;top:-5px !important;background:#fcfcfc !important;border:1px #999  !important;

-o-transition: .5s ease-out;
-webkit-transition: .5s ease-out;
-moz-transition: .5s ease-out;
}

.center_box + div > .pages2{display:none !important;}
.pages2{position:fixed !important;width:70px !important;right:0px !important;top:150px  !important;}
 
.pages2 span {display:block !important}
}
