css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain('www.facebook.com');
body, html,#content {background:#FFE0EC !important; background none 50% 0% scroll !important;color:#FF64B3!important}
#leftCol{background-color #FFFFFF !important; background:none fixed 20% 0% !important;background-repeat: repeat !important ;}
#contentCol { background:#FFE0EC !important; }
.profile .profile_color_bar  { background-color:#FFE0EC !important; }

#blueBar       { background:#FF88C4 !important; }
#pageLogo a    { background:url(http://i42.tinypic.com/fwlxdv.png) no-repeat scroll -21px 0 !important; }
#headNavOut    { background:#FFE0EC !important; border-color:#FF4EB1 !important; }
#headNav       { background-color:#FF88C4 !important; border-color:#FF4EB1 !important; }

#contentArea {
	color: #FF64B3; }
span.messageBody {
	color: #FF64B3;
	text-shadow: none 0 1px 0 !important; }
div.mall_post_body_text {color: #FF64B3;
	text-shadow: none 0 1px 0 !important; }


.jewelToggler  { background-image:url(http://i42.tinypic.com/fwlxdv.png) !important; }
#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,
#jewelMail:hover, #jewelMail:focus, #jewelMail:active,
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#f6b1c3 !important; }
#jewelCase .jewel  { border:none !important; }

#navSearch button  { background:url(http://i42.tinypic.com/fwlxdv.png) no-repeat scroll -1px 0 !important; }
#navSearch     { border-color:#FFE0EC !important; }


#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color: #f0bbd2 !important; color:#fc51ee!important; }


.lightblue_box  { background-color:#ff0099 !important; border-color:#f6b1c3 !important; }
.commentable_item .ufi_section  { background-color:#FFE0EC !important; }
textarea, .inputtext, .inputpassword  { border-color:#FF1493 !important; }


a  { color: #FF64B3 !important; }
.uiLinkButton input  { color:#FF64B3 !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#FF64B3 !important; }
button.as_link  { color:#FF64B3 !important; }


.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#f6b1c3 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#FF64B3 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#FF64B3 !important; }

.unread  { background-color:#f6b1c3 !important; }
.line  { color:#FF64B3 !important; }


#jewelCase .jewelFooter a   { background-color:#fce1e8 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color: #FF64B3 !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#FF64B3  !important; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#fce1e8 !important; border-color:#f0788c !important; }
#jewelCase .jewelBox  { background-color:#fff3f6 !important; }


.uiHeader h2  { color:#FF64B3 !important; }


.current_stuff td  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }


.UIComposer .UIComposer_Box  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }
.profile .box .box_header  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }


.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#FF88C4 !important; border-color:#f6b1c3 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#f6b0c2 !important; border-color:#edb9d4 !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#FF88C4 !important; color:#fce1e8  !important; border-color:#EE4785 !important; }


#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#fff3f6 !important;  }
#chat_tab_bar .chat_header  { background-color:#edb9d4 !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#FF1493 !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#f6b1c3 !important; color:#FF64B3 !important; }
#presence_bar .presence_menu_opts h2 span  { background:#edb9d4 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#edb9d4 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#fff3f6 !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#edb9d4 !important; border-color:#edb9d4 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#FF64B3 !important; }

#presence .presence_menu_opts  { border-color:#edb9d4 !important; }


.UIButton_Blue  { background:#FF64B3 !important; border-color:#edb9d4 !important; }
.UIComposer_InputArea  { border-color:#f6b1c3 !important; }
.uiButtonConfirm  { background:#FF5EB1 !important; border-color:#FF5EB1 !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#FF64B3!important; }

@-webkit-keyframes slidefromtoplarge {
from {
  opacity: 0;
  -webkit-transform: translateY(-200px);
}}
@-moz-keyframes slidefromtoplarge {
from {
  opacity: 0;
  -moz-transform: translateY(-200px);
}}
body:not(.fbQuestionPopupExpanded) .generic_dialog, body.fbQuestionPopupExpanded .pop_content {
  -webkit-animation: slidefromtoplarge 0.6s;
  -moz-animation: slidefromtoplarge 0.6s;
}

body.fbQuestionPopupExpanded .pop_content {  
  -webkit-box-shadow: 3px 3px 30px rgba(0,0,0,0.6);
  -moz-box-shadow: 3px 3px 30px rgba(0,0,0,0.6);
}
body.fbQuestionPopupExpanded {
  margin-right: 0px; 
  overflow: auto;
}
