css:
@-moz-document domain("www.minecraft.net") {
@media screen {
@font-face {
  font-family: 'Chau Philomene One';
  font-style: normal;
  font-weight: 400;
  src: local('Chau Philomene One'), local('ChauPhilomeneOne-Regular'), url('http://themes.googleusercontent.com/static/fonts/chauphilomeneone/v1/KKc5egCL-a2fFVoOA2x6tLCP4LGfprDLBfU97ANpU-o.woff') format('woff');
}
}

header {
 background: #4a3323;
 padding: 5px;
 border-bottom: solid black; 2px;
}



div#userbox {
   width: 200px;
}



header ul#menu {
 width: 535px;
}

header:hover ul#menu {
 margin: 5px;
}

header:hover ul li a {
   border-color: black;
   border-bottom-left-radius: 0px;
   border-bottom-right-radius: 0px;
}

header ul li ul li a::before {
  content: ">> ";
}

header ul li ul li a:hover {
   box-shadow: 1px 1px 50px black;
   -webkit-transform: scale(1.2) rotate(10deg);
}

header:hover a#logo {
  -webkit-transform: rotate(180deg);
  height: 100px;
}

header {
  height: auto;
}

header:hover a#logo {

 left: 0px;
 display: block;
 width: 100% !important;
 background-color: black !important;
 color: white !important;
}

a:hover::before {
  content: " - " attr(href);
}

a:hover {
   background-color: black;
   display: block;
}



img::after {
  content: attr(href);
}

header a#logo {
   -webkit-transition: all 0.5s ease-in-out;
   background-color: lightgray;
   position: relative;
   bottom: 0px;
   background-position: center;
   background-position-y: 0px;
   padding-top: 5px;
   padding-left: 10px;
   padding-right: 10px; 
   border-top-left-radius: 10px;
   border-top-right-radius: 10px;
}

header ul li a {
  font-weight: bold;
  text-transform: uppercase;
  background: green;
  padding: 5px;
  padding-left: 10px;
  padding-right: 10px;
  border-radius: 5px;
  border: solid white 2px;
}

footer {
  background: black;
  color: lightgray;
}

div#main > section:not(.social) {
  width: 100% !important;
  position: relative;
}

iframe {
width: 100% !important;
}

div#main > section:not(.social) p {
    position: absolute;
    bottom: 0px;
    padding: 10px;
    background: -webkit-linear-gradient(top left, green, rgba(0, 255, 0, 0));
}

section.social {
 position: relative;
 background: green;
 color: white;
 border-radius: 5px 15px;
 box-shadow: 1px 1px 5px gray;
 padding-top: 40px;
}

body {
  background: white;
  font-family: 'Chau Philomene One', sans-serif;
}

section.social > p, section.social > div {
   padding: 10px;
}

section.social h2 {
  background-color: #902707;
  padding-top: 5px;
  padding-bottom: 5px;
  padding-right: 10px;
  border-top-left-radius: 5px;
  border-bottom-left-radius: 5px;
  background-position-x: 5px;
  background-position-y: 5px;
  position: absolute;
  left: -50px;
  top: -20px;
}

a.tee { -webkit-transition: all 0.3s ease-in-out; font-size: 6px; }

a.tee span { -webkit-transition: all 0.3s ease-in-out; }
a.tee:hover span { top: 0px; position: absolute; -webkit-transform: rotate(-45deg); display: block; background: black; font-size: 25px !important; padding: 5px; padding-left: 10px; padding-right: 10px; left: -50px; width: 150px; border-radius: 5px; } 

a.tee:hover { 
    background-color: white;
    color: black;
    border-radius: 5px;
    box-shadow: 1px 1px 50px black;
    -webkit-transform: scale(1.2);
}


.store .item#game, .store .item#gift {
  width: 430px; 
}

.store .item#game { padding-right: 0px; }
.store .item#gift { padding-left: 0px; margin-left: 0px; }
}
