css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wolfsbos.nl") {

#menupart .mainMenuItemActive, #menupart .mainMenuItem a:hover {
    background: #C01010 !important;
}
#header {
    height: 0px !important;
}
#menu {
    background: url("http://www.photography-match.com/views/images/gallery/fall_leaves_003.jpg") no-repeat scroll 0 10%  !important;
    background-size: 100% !important;
    top: 30px !important;
}
#contentpart {
    margin-top: 60px !important;
}
#menupart .mainMenuItem:hover ul {
    background: url("http://www.photography-match.com/views/images/gallery/fall_leaves_003.jpg") !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a {
    background: url("http://www.photography-match.com/views/images/gallery/fall_leaves_003.jpg") scroll 0 10% !important;
}
#menupart .mainMenuItem:hover ul li:hover .subSubMenu:hover li a:hover {
    background: #C01010 !important;
}
body {
    background: url("http://img835.imageshack.us/img835/8376/lettersrood.png") repeat scroll center top transparent !important;
}
h1, h2, h3, h4, h5, h6 {
    color: darkred !important;
}
#content #contentpart .listtitle {
    background: #C01010 !important;
    border: 0px !important;
}
#content #contentpart .listrow2 {
    background: none repeat scroll 0 0 pink !important;
}
#content {
    background: url("http://img21.imageshack.us/img21/2314/sdm1.gif") no-repeat scroll center bottom transparent !important;
}
.widgetBottom form .button {
    background: #C01010 !important;
}
#copyright_text {
    font-size: 0 !important;
}
.nieuwskop {
    background: #C01010 !important;
}
.nieuwstitel a {
    color: black !important;
}
.nieuwsitem {
    background: pink !important;
}
#contentpart #objectWrapper692 .centerizer {
    border: 1px solid grey !important;
}


}
