/* Couleurs Pictos Banals */
.tid_userGoals .tid_goals .tid_mode_icons .tid_goal{
    background: #77452a !important;
    border: 1px solid #b37c4a !important;
}

/* Couleurs Pictos Rares */
.tid_userGoals .tid_goals .tid_mode_icons .tid_goal.tid_rare, .tid_userGoals .tid_goals .tid_top .tid_goal.tid_rare{
    background: #711600 !important;
    border: 1px solid #d59800 !important;
    box-shadow: 0px 0 2px #FE7D00 inset, 0 0 0px #644828, 0 0px 0px black !important;
}

/* Status Twinoid */
.tid_userStatus .tid_bubble {
    box-shadow: 0px 1px 1px #77452a !important;
}

/* couleur Texte "Que signifient ..?" */
.tid_userGoals .tid_goalHelp span.tid_help {
    border-bottom: 1px dashed #7e4d2a !important;
    color: white !important;
}

/* Footer Cadres 
.tid_userSheet .tid_footer .tid_footerRight, .tid_userGoals .tid_userGoals_footerLeft .tid_bgRight {
    background-image : url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/footerRight.png") !important;
}
.tid_userSheet .tid_footer, .tid_userGoals .tid_userGoals_footerLeft {
