.tid_userGoals .tid_userGoals_headerLeft .tid_bgRight {
    background-image: url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/headerRight.png") !important;
}
.tid_userGoals .tid_userGoals_headerLeft {
    background-image: url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/headerLeft.png") !important;
}

/* Description Pictos */
#tid_simplePointer .tid_inner, #tid_simpleTip .tid_inner {
    background-color: #77452a !important;
    border-radius: 4px 4px !important;
    border: 1px solid #b37c4a !important;
    outline: 0px !important;
}


/* couleur texte stats */
.tid_userGoals .tid_goals .tid_overall {
    color: #F0D79E !important;
    text-shadow: 0 0 0px #F0D79E;
}

.tid_userGoals .tid_userGoals_headerLeft .tid_bgRight {
    color: #DDAB76 !important;
    text-shadow: 0 0 0px #DDAB76;
}

.tid_userGoals .tid_goalHelp span.tid_help {
    color: #77452a !important;
}


/*###########################################################################
################################## FORUM ####################################
###########################################################################*/
/* Nouveau fond plus clair avec séparation */
 .saloon .forumcore {
    background-image:url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/core.png") !important;
}
 .saloon .forumfooter{
    background-image:url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/footer.png") !important;
}
 .saloon .forumheader{
    background-image:url("http://dl.dropbox.com/u/71292646/Codage/hordes-script/header.png") !important;
}

/*icone soleil*/
 .tid_newIcon.tid_recentIcon {
    background-image: none !important;
}
 #tid_forum_right .tid_bg1{
    background:#75482d !important;
}

/* barre jaune nouveau message */
 #tid_forum .tid_post.tid_unread .tid_body {
    border-left: 2px solid #A64823 !important;
}

/* En-tête post */
 #tid_forum .tid_post .tid_header {
    background-color: #965C36 !important;
}


/* On arrondit les bords */
 #tid_forum table.tid_threads tr.tid_dateSeparator td {
    border-radius: 4px 4px 0 0 !important;
}

 .tid_buttonBar a {
    background-color: #77452a !important;
    border-top: 0px solid #b37c4a !important;
    border-radius: 3px 3px !important;
    margin-right: 2px !important;
    margin-left: 2px !important;
    border: 1px solid #b37c4a !important;
    outline: none !important;
}

 .tid_pagination div.tid_cur {
    background-color: #77452a !important;
    border-top: 0px solid #b37c4a !important;
    border-radius: 3px 3px !important;
    border: 1px solid #b37c4a !important;
    outline: none !important;
    width: 80px !important;
}

#tid_forum .tid_actionBar .tid_buttonBar{
    padding-right: 0px !important;
}

.tid_sharer .tid_url {
    color: white !important;
}

.tid_sharer {
    background: #b37c4a !important;
    border-radius: 4px !important;
    border: 1px solid #77452a !important;
}

.tid_sharer a.tid_close {
    background:  #77452a !important;
    border: 1px solid #A37F1a !important;
    outline: 0px solid #868483 !important;
}

.tid_sharer .tid_url input {
    border: 1px solid #9F9D9B !important;
    box-shadow: 0 1px 0px #C7C5C0 inset !important;
}

/* couleur annonces oracles */
#tid_forum .tid_threadNotice.tid_announce {
    background: #77452a !important;
    border: 1px solid #b37c4a !important;
    box-shadow: 0px 0 2px #FE7D00 inset, 0 0 0px #644828, 0 0px 0px black !important;
}

/* couleur annonces admin */
#tid_forum .tid_threadNotice.tid_adminAnnounce {
    background: #711600 !important;
    border: 1px solid #b37c4a !important;
    box-shadow: 0px 0 2px #FE7D00 inset, 0 0 0px #644828, 0 0px 0px black !important;
}

/* balise RP */
.tid_editorContent .tid_roleplay {
    max-width: 90% !important;
    background-color: #77452a !important;
    border-radius: 4px 4px !important;
    border: 1px solid #b37c4a !important;
    color: white !important;
    font-size: 10pt !important;
    font-style: normal !important;
}

 .tid_editorContent .tid_roleplay .tid_wroleplay {
    background-image: none !important;
 }
 
/* Gras RP */
.tid_editorContent .tid_roleplay strong {
    color: #AFB3CF !important;
    }

/* bouton envoyer message */
.tid_postForm .tid_bg1 button {
    background-color: #77452a !important;
    border: 1px solid #b37c4a !important;
    outline: 0px !important;
}
 
/* pseudos users */
 .tid_user.tid_userBg {
    background-color: #77452a !important;
    border-top: 1px solid #b37c4a !important;
 }
 .tid_user.tid_userUnknown {
    background-image: none !important;
}
 .tid_user.tid_userBg.tid_userFriend{
    background-color: #711600 !important;
    border-top: 1px solid #b37c4a !important;
 }
 .tid_user.tid_userFriend {
    background-image: none !important;
}
 .tid_user {
    padding-right: 5px !important;
 }

 /* décalage chrome */
 #tid_forum_right .tid_containerWrapper .tid_forumThread, #tid_forum .tid_postForm {
    padding-left: 15px !important;
}
 #tid_forum_right .tid_containerWrapper .tid_forumThread, x:-moz-any-link {
    padding-left: 2px !important;
}
  
 #tid_forum .tid_forumRules {
    position: relative !important;
    left: 10px !important;
}

 #tid_forum .tid_forumRules, x:-moz-any-link {
    position: relative !important;
    left: 2px !important;
}

