css:
@-moz-document domain("fuelphp.com") {
body, p, pre, abbr, dl, dt, dd, ol, ul, li, span, small, a  { font-family: "shanti", sans-serif }

@font-face {
  font-family: 'Shanti';
  font-style: normal;
  font-weight: normal;
  src: local('Shanti'), url('http://themes.googleusercontent.com/static/fonts/shanti/v4/YXI2DWvNHH4E3kHi_10pZw.woff') format('woff');
}
@font-face {
  font-family: 'Droid Sans';
  font-style: normal;
  font-weight: bold;
  src: local('Droid Sans Bold'), local('DroidSans-Bold'), url('http://themes.googleusercontent.com/static/fonts/droidsans/v3/EFpQQyG9GqCrobXxL-KRMQFhaRv2pGgT5Kf0An0s4MM.woff') format('woff');
}

pre, code {
font-family: "Monaco", monospace;
color: #DDD;
text-shadow: 1px 1px 0px #333;
font-size: 13px;
margin: 0 0 25px 0;
background-color: #444;
padding: 10px;
}

p, ul, ol {
color: #444;
text-shadow: 1px 1px 0px white;
font-size: 15px;
margin: 0 0 23px 0;
line-height: 1.5em;
}

ul {
list-style: disc;
padding: 0 0 0 35px;
}

#twitter { list-style: none }
#twitter a { font-family: "droid serif"; serif; }

h1, h2, h3, h4 {
 font-family: "Droid Sans", sans-serif; text-transform: uppercase;
 font-weight: bold;
}

h2, h1 { font-size: 20pt }

h3, h4 { font-size: 18pt }
}
