css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("previous.delicious.com") {

/*-- main content --*/
#main .content {
   padding-top: 0px !important;
   }

.tabs {
   height: 20px !important;
   top: -20px !important;
   }

.tabs a {
   line-height: 20px !important;
   padding-bottom: 0px !important;
   }

.pills {
   display: none !important; /*hides 'links' & 'stacks' buttons*/
   }

.userPageFilter {
   border-top: 1px solid #ddd !important;
   }
   
#tagscope {
   margin-bottom: -20px !important;
   }

#bulkEditActions {
   margin-top: 25px !important;
   }
   
/*-- link appearance --*/

.link .thumb {
   display: none !important; /* hide picture in description */
   }

.link .title img {
   margin-right: 10px !important;   /* only affects display if you are using favicons (http://userscripts.org/scripts/show/118892) */
   }
   
.link .action.share {
   display: none !important; /*remove share link button*/
   }

/* show entire descriptive note (no 'click to see more' bar) */
.link .note {
   height: 100% !important;
   }
   
.moreless {
   display: none !important;
   }

/*-- tag appearance in link description --*/
.link .tag {
   border: none !important;
   color: #444ccc !important;
   background-color: transparent !important;
   margin-left: 5px !important;
   margin-bottom: 0px !important;
   }

.link .tag .ar {
   display: none !important;
   }

.link .tag .co {
   border: none !important;
   }

/*-- sidebar userpic size and position --*/

.sidebar .sidebarProfilePic {
   width: 40px !important;
   height: 40px !important;
   float: right !important;
   }

.sidebar .sidebarProfilePic img {
   width: 40px !important;
   height: 40px !important;
   float: right !important;
   }

/* size of username - can be adjusted to fit space next to userpic or make bigger to sit beneath picture */
.sidebar h3 {
   font-size: 12px !important;   
   }

/* spacing in sidebar on feeds page */
.sidebar h4 {
   margin-bottom: 40px !important;
   }
}
