@-moz-document domain('deviantart.com') {

/* This tiny, pathetic style was written by Abfc */
/* http://abfc.deviantart.com */

body a {
text-decoration: none !important;
}

body a:visited {
text-decoration: none !important;
}

body a:hover {
text-decoration: none !important;
}

body a:active {
text-decoration: none !important;
}

div.bubbleview a {
text-decoration: none;
}

div.bubbleview a:hover {
