css:
@-moz-document domain('plus.google.com') {
/* ================ */
/*  ヘッダ部の変更  */
/* ================ */
div.k-wb-nb, div#gbx1 {
	/* ヘッダ全体 */
	border-top	: none !important;
	background	: -moz-linear-gradient(top, #FFF, #09E) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#09E)) !important;
}
input[id="searchBox"], div#gbqfqw {
	/* 検索ボックス */
	border			: none !important;
	border-radius	: 20px !important;
	background		: -moz-linear-gradient(top, #6CF, #FFF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#6CF), to(#FFF)) !important;
}

/* ================================ */
/*  アイコンを鏡面に（Chromeのみ）  */
/* ================================ */
a.g-s-n-aa > img:not([hc='off']) {
	/* プロフィールリンクホバー時のアイコンは除外 */
	-webkit-box-reflect		: below 4px -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(.7, transparent), to(white)) !important;
	-webkit-border-radius	: 3px !important;
	border-radius			: 4px !important;
	border					: 0px !important;
	position				: relative !important;
}

/* ============== */
/*  背景色を変更  */
/* ============== */
div#contentPane {
	/* 基本 */
	background-color	: #EFF !important;
}
div#content > div:last-child > div {
	/* 基本と右カラムの境界線 */
	border-right	: 1px solid transparent !important;
}
div#content > div > div:nth-child(2) > div:last-child {
	/* 左メニューフキダシの△部分 */
	background-color	: #EFF !important;
}

/* TODO: 検索結果画面でナビバーに食い込むフキダシ△部分 */

div[role='navigation'] + div div > div[guidedhelpid='sharebox'] + div {
	/* 共有フキダシの△部分を消す */
	background	: none !important;
}
div[id^='update-'] > div > div > div:nth-last-child(3) {
	/* 各ポストフキダシの△部分を消す */
	background	: none !important;
}
div[guidedhelpid='sharebox'] > div > div:last-child div.jPtLsf {
	/* イベント招待共有のキャンセルボタン横の謎の三角 */
	display	: none !important;
}
div[guidedhelpid='sharebox'] > div > div:last-child div.nD4xle {
	/* イベント招待共有のキャンセルボタン */
	background-color	: rgba(204, 238, 255, .9) !important;
	border-radius		: 0px 0px 0px 17px !important;
	text-shadow		: 2px 2px 2px #EFF;
}
div[role='navigation'] > div > div {
	/* 画面上部のナビゲーション部 */
	background-color	: #EFF !important;
}
nav[role='navigation'] > div > div {
	/* 画面上部のナビゲーション部（ローカル ページ） */
	background-color	: #EFF !important;
}
div#content > div:first-child {
	/* ページを管理 ページの下部背景色 */
	background-color	: #EFF !important;
}

div[role='navigation'] + div div > div[guidedhelpid='sharebox'] > div {
	/* 共有フキダシ */
	background	: -moz-linear-gradient(top, #CEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#CEF), to(#FFF)) !important;
}

div[guidedhelpid='streamcontent'] > div:nth-last-child(3),
div[id^='update-'] {
	/* 各投稿ブロックの後ろ */
	background-color	: transparent !important;
}
div[id^='update-'] > div > div:first-child > div > header > h3 {
	/* 投稿者の名前を大きく表示 */
	font-size	: x-large !important;
}
div[guidedhelpid='streamcontent'] > div > div[id^='update-'] > div > div:first-child > div:nth-last-child(2) {
	/* 投稿者の名前などをデフォルトより少し上に詰める（ストリーム上のみ） */
	padding-top	: 10px !important;
}
div[id^='update-'] > div.qf > div:first-child > div:last-child > div[role='button']:not(:last-child) {
	/* +1, 共有, Hangoutボタン */
	border-color	: transparent !important;
}
div[role='navigation'] + div > div > div {
	/* プロフィールページ投稿ブロック後ろ */
	background-color	: #EFF !important;
}
div[token^='photos'] > div:last-child {
	/* アルバムサムネイル画面後ろ */
	background-color	: #EFF !important;
}
div.oz-sg-contacts > div.r3 {
	/* サークル管理画面、「操作」「歯車」部分背景色 */
	background-color	: #EFF !important;
}

/**************/
/* 通常の投稿 */
/**************/
div[id^='update-'] > div:first-child.qf > div:not(:last-child) {
	/* 投稿本文の背景色 */
	background	: -moz-linear-gradient(top, #CEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#CEF), to(#FFF)) !important;
}
div[id^='update-']:hover > div:first-child.qf > div:first-child > div:last-child > div[role='button']:not([aria-pressed='true']):not(:last-child) {
	/* +1, 共有, Hangoutボタン（投稿ホバー時） */
	border-color	: #BEF !important;
	background		: -moz-linear-gradient(top, #FFF, #AEF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#AEF)) !important;
}
div[id^='update-'] > div:first-child.qf > div:first-child > div:last-child > div[role='button']:not([aria-pressed='true']):not(:last-child):hover {
	/* +1, 共有, Hangoutボタン（ボタンホバー時） */
	border-color	: #BEF !important;
	background		: -moz-linear-gradient(top, #FFF, #7EF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#7EF)) !important;
}
div[id^='update-'] > div:first-child.qf > div:first-child > div:last-child > div.ml:hover {
	/* アクティビティを表示ボタン */
	background		: -moz-linear-gradient(top, #FFF, #CEF) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#CEF)) !important;
}
div[id^='update-'] > div:first-child.qf > div.FE {
	/* コメント／アクティビティ */
	background		: -moz-linear-gradient(top, #CEF, #FFF) !important;
	background		: -webkit-gradient(linear, left top, right bottom, from(#CEF), to(#FFF)) !important;
}
div[id^='update-'] > div:first-child.qf > div.FE > div > div:first-child,
div[id^='update-'] > div:first-child.qf > div.FE > div > div:first-child > div.iP,
div[id^='update-'] > div:first-child.qf > div.FE > div > div:first-child > div.iP > * {
	/* コメント／アクティビティ */
	background-color	: transparent !important;
}
div[id^='update-'] > div:first-child.qf > div.FE > div.rs > div.f4,
div[id^='update-'] > div:first-child.qf > div.FE > div.rs > div.f4 > * {
	/* アクティビティを非表示 */
	background-color	: transparent !important;
}
div[id^='update-'] > div:first-child.qf > div.FE > div.dw > div.bI {
	/* 「コメントを追加」ボックス */
	border-radius		: 0px 0px 7px 7px !important;
	margin-right		: 0px !important;
	background-color	: #FFF !important;
}
/**************/


/**************/
/* 注目の投稿 */
/**************/
div[id^='update-'] > div:first-child > div.UJ {
	/* 「Google+ で注目の投稿」 */
	background	: -moz-linear-gradient(top, #FEE, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#FEE), to(#FFF)) !important;
}
div[id^='update-'] > div:not(:first-child).qf > div:not(:last-child) {
	/* 投稿本文の背景色 */
	background	: -moz-linear-gradient(top, #FEE, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#FEE), to(#FFF)) !important;
}
div[id^='update-']:hover > div:not(:first-child).qf > div:first-child > div:last-child > div[role='button']:not([aria-pressed='true']):not(:last-child) {
	/* +1, 共有, Hangoutボタン（投稿ホバー時） */
	border-color	: #FDE !important;
	background		: -moz-linear-gradient(top, #FFF, #FCE) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#FCE)) !important;
}
div[id^='update-'] > div:not(:first-child).qf > div:first-child > div:last-child > div[role='button']:not([aria-pressed='true']):not(:last-child):hover {
	/* +1, 共有, Hangoutボタン（ボタンホバー時） */
	border-color	: #FDE !important;
	background		: -moz-linear-gradient(top, #FFF, #F9E) !important;
	background		: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#F9E)) !important;
}
div[id^='update-'] > div:not(:first-child).qf > div:first-child > div:last-child > div.ml:hover {
	/* アクティビティを表示ボタン */
	background	: -moz-linear-gradient(top, #FFF, #FEE) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#FEE)) !important;
}
div[id^='update-'] > div:not(:first-child).qf > div.FE {
	/* コメント／アクティビティ */
	background	: -moz-linear-gradient(top, #FEE, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#FEE), to(#FFF)) !important;
}
div[id^='update-'] > div:not(:first-child).qf > div.FE > div > div.tr,
div[id^='update-'] > div:not(:first-child).qf > div.FE > div > div.tr > div.iP,
div[id^='update-'] > div:not(:first-child).qf > div.FE > div > div.tr > div.iP > * {
	/* コメント／アクティビティ */
	background-color	: transparent !important;
}
div[id^='update-'] > div:not(:first-child).qf > div.FE > div.rs > div.f4,
div[id^='update-'] > div:not(:first-child).qf > div.FE > div.rs > div.f4 > * {
	/* アクティビティを非表示 */
	background-color	: transparent !important;
}
div[id^='update-'] > div:not(:first-child).qf > div.FE > div.dw > div.bI {
	/* 「コメントを追加」ボックス */
	border-radius		: 0px 0px 7px 7px !important;
	margin-right		: 0px !important;
	background-color	: #FFF !important;
}
/**************/

body.u0b div[id^='update-'] > div > div:first-child > div > header {
	/* 通知欄では投稿者の情報エリアの高さを多めに取る */
	height	: 70px !important;
}
body.u0b div[id^='update-'] div[emoticons='emoticonsReplaced'],
body.u0b div[id^='update-'] div[emoticons='emoticonsReplaced'] + span[role='button'] {
	/* 通知本文のpaddingを無効化 */
	padding	: 0px !important;
}
div.NB h3,
div.NB h3 + span,
div.NB h3 + div > div.Ncb,
div.NB h3 + a[href='events'] {
	/* 「友だち候補」などのタイトル文字／「すべて表示」ボタン／自プロフィールの公開範囲アイコン */
	background-color	: #EFF !important;
}
div.oz-sg-people,
div.oz-sg-contacts > div.CW,
div.oz-sg-contacts > div.CW > div {
	/* サークル管理画面の背景 */
	background-color	: #EFF !important;
}
.z3 {
	/* 「あなたのサークルには未追加」などのタイトル文字 */
	background-color	: #EFF !important;
}
div#contentPane > div > nav + div > div > div {
	/* 投稿個別画面の背景 */
	background-color	: transparent !important;
}
div.SM > div > div.cQ {
	/* サクられ通知のユーザブロック */
	border		: 1px solid #BEF !important;
	background	: -moz-linear-gradient(top, #FFF, #BEF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#BEF)) !important;
}
div[guidedhelpid='streamcontent'] > div:nth-last-child(2) {
	/* 「もっと見る」ボタン */
	border		: 1px solid #BEF !important;
	background	: -moz-linear-gradient(top, #BEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#BEF), to(#FFF)) !important;
}
span.proflinkWrapper > a {
	/* メンションの色 */
	/* いいのがない */
}
a.ot-hashtag {
	/* ハッシュタグの色 */
	/* いいのがない */
}

.zu:hover {
	/* プロフィール変更時のカーソルホバー */
	background-color	: #CEF !important;
}
div.Bka {
	/* Googleコンタクトからの連絡先情報 */
	border		: 1px solid #BEF !important;
	background	: -moz-linear-gradient(top, #FFF, #BEF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#BEF)) !important;
}
input.bua {
	/* 公式右上さん下のテキストボックス */
	border		: 1px dashed #ACF !important;
}
div[role='slider'] > div:first-child {
	/* スライダーの目盛 */
	border-left	: 6px solid #EFF !important;
	border-right	: 6px solid #EFF !important;
}


/* ========== */
/*  イベント  */
/* ========== */
div[eventid].fRa {
	/* イベント一覧の背景 */
	background	: -moz-linear-gradient(top, #FFF, #BEF) !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(#BEF)) !important;
}
div[token^='events/'] div.nZa {
	/* イベント発言フキダシ */
	background	: -moz-linear-gradient(top, #CEF, #FFF) !important;
	background	: -webkit-gradient(linear, left top, right bottom, from(#CEF), to(#FFF)) !important;
}
div[token^='events/'] div.c6a {
	/* イベント発言フキダシの△部分を消す */
	background	: none !important;
}
div[token^='events/'] div.qu {
	/* 参加状況 */
	background-color	: #EFF !important;
}


/* ============ */
/*  共有ボタン  */
/* ============ */
/* 一般公開／友だちの友だちサークル　ボタンを赤に */
span.i-j-h-ma-xi {
	background	: -moz-linear-gradient(center top , #F66, #AF301F) repeat scroll 0 0 transparent !important;
	background	: -webkit-gradient(linear, left top, left bottom, from(#F66), to(#AF301F))!important;
	border		: 1px solid #F33 !important;
}
span.i-j-h-ma-xi > span.i-j-h-ma-ib {
	border-color	: #F66 !important;
}
/* 「共有」「コメントを投稿」などのボタンを青に */
div[role="button"].c-b-da {
	background	: #4D90FE !important;
	border		: 1px solid #3079ED !important;
}

/* ================ */
/*  スクロールバー  */
/* ================ */
::-webkit-scrollbar-thumb {
	background-color : rgba(102, 204, 255, .3) !important;
}
::-webkit-scrollbar-thumb:hover {
	background-color : rgba(102, 204, 255, .7) !important;
}
} /* moz-documentブロック終 */
