css:
@-moz-document url("http://finance.google.com"), url("https://finance.google.com"), url("http://www.google.com/finance"), url("https://www.google.com/finance") {
body 
    {
    background-color:#000000 !important;
    color:#BDBDBD !important;
    }
a, a:visited, a:link 
    {
    color:#848484!important;
    }

a img 
    {
    border-color:#4BCC56 !important;
    }
#gbu
    {
    display:none;
    }

#gbx1, #gbx2, #gbqm, #gb
    {
    background:#000000 !important;
    color:#FFFFFF !important;
    border-color:#000000 !important;
    }

#gbqfqwb, .fjfe-autocomplete-ticker
    {
    background:#000000 !important;
    }

.fjfe-recentquotes, .fjfe-recentquotes-show-perc, .fjfe-nav, .fjfe-nav-nav .fjfe-recentquotes-quotes, .fjfe-nav-ra, .fjfe-recentactivities, .fjfe-nav-sub, .fjfe-nav-selected, .fjfe-nav-item a
    {
    background:#000000 !important;
    }

.unchanged, .chb, .fjfe-recentquotes-show-perc, .fjfe-perc-toggle, .fjfe-chg-toggle, .goog-link-button, .fjfe-searchbox-input, .fjfe-autocomplete-ticker .name
    {
    color: #BDBDBD !important;
    }

.gbqfb
    {
    background:#000000 !important;
    border-color: #BDBDBD !important;
    }
.gbqfb-hvr
    {
    background:#4D90FE!important;
    border-color: #4D90FE!important;
    }
.goog-tab-selected a.t b.t 
    {
    background: transparent !important;
    }

.down, .chr
    {
    color: #FE2E2E !important;
    }

.up, .chg
    {
    color: #04B404 !important;
    }

img, .goog-custom-button .icon-up, .goog-custom-button .icon-down
    {
    opacity:0.7 !important;
    }

.news-item, .g-section, .g-tpl-50-50, .g-split, .gf-control, .gf-table tr th
    {
    background-color: #000000 !important;
    }
}
