css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("armorgames.com") {

#gamefilearea {
	display:block !important;
}

#afg_preloader,
#leaderboard,
#adbar,
#gameleaderboard,
.loggedoutad,
#homepagesquare,
#homepagesquare2,
#randomgame,
#premiumhome,
#adblock,
#adblocktwo,
#footer,

#sidebar,



.ad-leaderboard,
.ad-medrec

{
	display:none !important;
	visibility:hidden !important;
	width:0 !important;
	height:0 !important;
	position:absolute !important;
	left:-999em !important;
	line-height:0 !important;
	font-size:0 !important;
	overflow:hidden !important;
}

#page-store #main,
#featuredgame,
.group #tags,
#page-category #main,
#page-generic #main {
	width:auto !important;
}

#registrationform {
	width:690px;
}

#page-generic #main,
#page-store #main,
#featuredgame {
	background-image:url(http://armorgames.com/images/bgr-corner-large-01.png) !important;
	float:none !important;
}
#popularcatgames,
#categorylisting {
	background-image:url(https://lh6.googleusercontent.com/-LP_bX5eLGDA/T4ZB8wPATnI/AAAAAAAAAlI/8bxFMemH970/s950/bgr-corner-category-01.png);
}

#gamefilearea {
	margin-bottom:20px;
}

#content {
	background:none;
}

.gamelisting {
	height:300px;
}

body[class] {
	background:#003 !important;
}

/*[[LiteArena]]*/

}

style_setting_options.value:
/* LITE GAME AREA */

#primary-nav,
#secondary-nav,
#search-form,
#news-nav,
#profile-content,

#page-game #subgame,
#page-game .gamearea_header,
#page-game #header-top a,
#page-game #profileblock,
#page-game #header-nav,
#page-game #header-bottom {
	display:none !important;
}
#page-game #wrapper,
#page-game #header,
#page-game #gamearea {
	background:none;
	position:relative;
}
#page-game #header,
#page-game #header h1,
#page-game #header h1 a {
	display:block;
	float:none;
	width:auto;
	height:80px;
	background-position:50% 50%;
	margin:0;
	padding:0;
}

#page-game #content .ex-breadcrumb {
	position:absolute;
	bottom:0;
	left:0;
	width:100%;
	text-align:center;
	margin:0;
	padding:0;

	color:#fff;
}
#page-game #gamearea object,
#page-game #gamearea embed {
	display:inline-block;
	background:#000;
}


#ag3-header {
	height:64px;
}

#page-game #ag3-header {
	background:none;
}
#ag3-header #player-account .home-link {
	margin:0;
	padding:0;
	width:100%;
	background-position:50% 50%;
}
