css:
@-moz-document domain("toodledo.com") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(http://fonts.gstatic.com/s/roboto/v15/oMMgfZMQthOryQo9n22dcuvvDin1pK8aKteLpeZ5c0A.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

html, table, .task, #viewby, #tabs, #tip, .tab a, .tabon a, .dett, #colhead, #tasks, #filterPop, #sortpop, #sortpop_p, #searchform, #searchtask, #addtask, .field b, #locationlist, #locform, .formbox p, .simplebox, #answer, #answer p, .connectbox, .detail, #duediv, #repdiv, #repdiv i {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
    border: 0;
    color: #2F2020 !important;
}

.rownew{background-color:#F2FCBA;}

body, h1, h2, p, .notool, form, hr {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
    color: #e6e6e6 !important;
}

body.guest, table .newRow th  {
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
}

#ideas, .ade-popup.dropdown-menu.open, #duediv, #actiondiv {
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
    box-shadow: 0 0 15px #211;
  	border: 0;
}

#duediv {font-size: 110%}
#duediv b, .quickdates {color: #e6e6e6 !important;}
.quickdates a {color: #DEBE68 !important;}


.detail b, i {
    color: #e6e6e6 !important;
}

a, #ideas li {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
    color: #b3947f !important;
    text-decoration: none !important;
}

a:hover {
    color: #A65016 !important;
    text-decoration: none !important;
}

.tab, #notebooks, #notebook_details, ul, li, .popmenu {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
}

.popmenu
{
    background: #835D3B url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left 0px;
    border-left: 1px solid #b3947f;
    border-right: 1px solid #b3947f;
    border-bottom: 1px solid #b3947f;
    color: #b3947f !important;  
}

.popmenu label{
    color: #b3947f !important;  
}

.tab, .tabon {
    background-image: url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
	width: 152px;
    margin-bottom: 3px !important;
    border: 0;
}

.tab {
    box-shadow: inset -2px 0 5px #544;
}


#rule1, #rule2, #rule3, #rule4, #rule5, #rule6 {
    background-color: #927C4A !important;
}

#tlbr_search, #tlbr_sort, #tlbr_icons, #tlbr_show, #tlbr_quick, #tlbr_multi, #head, #colhead, .addbox {
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
    border: 0 !important;  
}

#tlbr_search {
     box-shadow: 2px -2px 2px #300;
}

.ng-scope #tlbr_search{
     width: 990px ;     
     box-shadow:  0 0 0 #000;
}

#colhead, #colhead a, #rchunk a.shove {
    color: #b3947f;
  
}

#colhead div {
  	border: 0;
    padding-right: 1px;
   	
}

#tlbr_new {
    text-shadow: rgba(0,0,0,0.8) 0 0 2px;
    background: #835D3B url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left 0px;
    color: #DEBE68;
  	border-right: 0;
}

.sep {
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
    border: 0 !important;
    color: rgb(200, 200, 200) !important;
}

.colc {
    border: 0 !important;
}



#searchtask, #addtask {
    background: #fcf6b3 url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
    width: 960px;
}

#banner {
    background: transparent !important;
        /*background: transparent url(https://sites.google.com/site/diegomaranan/home/2do.png) no-repeat left bottom !important; */;
}

#main, body, a.banbut, table .colhead th {
    background: #663300 url(https://sites.google.com/site/diegomaranan/home/2Do_leather.jpg) repeat left top !important;
}

#colhead {
    box-shadow: 2px 0 5px #300;
    border-bottom: 1px solid rgb(180, 175, 160) !important;
}

#head {
    box-shadow: 0 -1px 2px #300;

}


#tasks .sep {
    text-shadow: rgba(0,0,0,0.5) 0 0 2px;
}

#tasks .cols.row {
    padding-left: 3px;
    text-shadow: rgba(255,255,255,0.93) 0 0 2px;
}

#tasks i {
    color: rgb(40, 40, 40) !important;
}

#head {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
    color: #DFDFDF !important;
    width: 970px !important;
}

#head, .tab, .tabon {
    border-bottom-left-radius: 8px;
    border-top-left-radius: 8px;
    border-top: 0 !important;
}

#rchunktop {
    margin-left: -15px !important;
    border-bottom: 0 !important;
}

#rchunk {
    margin-left: -4px !important;
    padding-left: 0 !important;
}

#sec_task, #sec_notebook, #sec_outline, #sec_list, #sec_habit, a.banbut {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif !important;
    color: #b3947f;
    border-top-left-radius: 13px;
    border-top-right-radius: 13px;
    border: 0;
    box-shadow: 0 -2px 3px #300;
    text-shadow: rgba(0, 0, 0,0.9) 0 0 2px;
    margin: 16px 0 0 0;
}

#sec_task, #sec_notebook, #sec_outline, #sec_list, #sec_habit {
    background: none;
}

.sec_btn_sel {
     background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
     font-weight: normal; 
}

#sec_organize, #sec_tool, #sec_help, .popmenu li {
    text-shadow: rgba(0, 0, 0,0.9) 0 0 2px;
    margin-top: 13px;
}

          
        
#rchunk {
    background: #663300 url(https://sites.google.com/site/diegomaranan/home/2Do_leather.jpg) repeat left top !important;
    border-bottom-left-radius: 13px;
    border-bottom-right-radius: 13px;
}

#left_side {
    background: #663300 url(https://sites.google.com/site/diegomaranan/home/2Do_leather.jpg) repeat left top !important;
    margin-right: 0 !important;
    padding-right: 0 !important;
}

#viewby, #viewby a, #tip, .viewby, #welcome, #proflinks, #proflinks a {
    font-family: 'Roboto', 'Trebuchet MS', Verdana, sans-serif;
    color: #b3947f !important;
    border: 0;
    text-shadow: rgba(0, 0, 0,0.7) 0px 0x 2px;
}

#tabs {
    margin-left: 7px !important;
}

#viewbybelow {
    text-shadow: rgba(0,0,0,0.5) 0 0 2px;
}

.tabon, #tasks, td, tr, #locationlist {
    background: #fcf6b3 url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
}

#notebooks .row.sel, #notebooks .rownew.sel{
  background: #F2fCBA url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
}

#tasks, #notebook_details {
    border-bottom-left-radius: 13px;
    border-bottom-right-radius: 13px;
  	border: 0;
}

#userstylewarning, #bottom, #logo {
    display: none;
}

.success {
    color: rgb(50, 200, 100) !important;
}

.popmenu li {
    color: #b3947f;
    border-top: 1px solid #b3947f;
}

#notebooks .row, #notebookTitle  {
    color: #2F2020;
    background: #fcf6b3 url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
    box-shadow: 2px 0 5px #300;
}
    
  
#details{
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
	border-top-right-radius: 13px;
    	border-bottom-left-radius: 13px;
  	border-bottom-right-radius: 13px;
 	box-shadow: rgb(34, 17, 17) 0px 0px 15px 0px;
    margin-top: 7px;
    padding-left: 6px;
}
  
  
#outline  {
    color: #2F2020;
    background: #fcf6b3 url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
}

#details p, #details i {
    color: #b3947f !important;
}

#notebookActions{
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
}
  

#notebook_details {
    background: #F2fCBA url(https://sites.google.com/site/diegomaranan/home/paper.png) repeat left top !important;
    color: #2F2020;
  	margin-top: -1px;
}


#details .title, #details .keywords, #details .note {
    border-top: 0;
    border-right: 0;
    border-left: 0;
    border-bottom: 1px dotted #b3947f;
}

.ng-scope.tab, .ng-scope.tabon {
    border-top: 0 !important;
    box-shadow: 0 0 0 #000;
}

#addListForm{
	border-radius: 13px;
}

.ng-scope p, #addListForm{
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
   	padding: 10px;
  	border-bottom-left-radius: 13px;
  	border-bottom-right-radius: 13px;
 	box-shadow: rgb(34, 17, 17) 0px 0px 15px 0px;
}

div.ng-scope.tab{
	background: none;
 	box-shadow: rgb(34, 17, 17) -4px 0px 6px 0px;
	border: 0;
}
div.ng-scope.tabon{
    background: #fff url(https://sites.google.com/site/diegomaranan/home/2Do_cloth.jpg) repeat left top !important;
	border: 0;
 	box-shadow: rgb(34, 17, 17) -4px 0px 6px 0px;
}
div.ng-scope.tabon a, div.ng-scope.tab a{
  	color: #b3947f !important;
}


#ideas p {
  	box-shadow: rgb(0, 0, 0) 0 0 0 0;
   	background: none;
}

#details .dates {
  	color: #b3947f;
}
}
