css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document regexp('^htt(p|ps):\/\/plus\.google\.(com|ru)/[^\d{5,}]*$') {
#contentPane>div>div:nth-child(2){width:88%!important}
#contentPane>div>div:last-child{display:none!important}
div[id^="update"],div[guidedhelpid^="streamcontent"]{width:100%!important}
div[id^="update"]>div>div>div:nth-child(5)>div>div>div{margin:0 auto!important}
div[itemtype*="VideoObject"]>div>div{margin:0 auto!important}

div[id^="update"]>div>div:last-child>div:nth-child(2)>div:last-child>div>div:hover div[id^="z1"] div{color:/*[[cch]]*/!important}
div[id^="z1"] div{color:/*[[cc]]*/!important}
}
