css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("www.foopets.com") {



#page, #content, #pet_info
{ background: transparent !important; }

body{background:#000000 url('http://i1080.photobucket.com/albums/j338/mishkalovers/Twilight_Symbolism-1.jpg') fixed repeat repeat; background-position:center; !important}
	

a
{ color: #FF0000 !important; }


h1, h2, h3

{ text-shadow: 1px 2px 2px #000 !important; 
color: #FFFFFF !important;
font-size: 32px !important; }


#nav li.selected a, #nav li a:hover
{ background: transparent !important;
color: #000000 !important; }


#nav_footer li
{ color: #000000 !important;
font-size: 15px !important;
border: none !important; }


.foobutton
{ background: #000000 !important;
-moz-box-shadow: rgba(0,0,0, .8) 0px 4px 18px 2px !important; }

.foobutton:hover
{ border-color: #FFFFFF !important;
-moz-box-shadow: inset rgba(98,128,136,0.5) 0px 2px 8px 2px, rgba(98,128,136,0.5) 0px 2px 12px 2px !important;  }

.foobutton span
{ font-size: 15px !important;
color: #FF000 !important;
text-shadow: 1px 2px 2px black, 1px 2px 2px black !important; }

#page
{ border-bottom: none !important; }

#subnav ul li{
    font-size:13px;
    height:28px;
    line-height:28px;
    padding:0;
    margin:0;
    border-right:1px solid #000000;
    border-left:1px solid #000000;
    background: #000000;/* old browsers */
    background: -moz-linear-gradient(top, #000000 0%, #000000 50%, #000000 50%, #000000 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(50%,#000000), color-stop(50%,#000000), color-stop(100%,#000000));  /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000000', endColorstr='#000000');
  !important}

#subnav ul li:hover{
    background: #000000;/* old browsers */
    background: -moz-linear-gradient(top, #000000 0%, #000000 50%, #000000 50%, #000000 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(50%,#000000), color-stop(50%,#000000), color-stop(100%,#000000)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000000', endColorstr='#000000');
  !important}

  #subnav ul li.selected{
    background: #FF0000;/* old browsers */
    background: -moz-linear-gradient(top, #ff0000 0%, #ff0000 50%, #ff0000 50%, #ff0000 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(50%,#000000), color-stop(50%,#000000), color-stop(100%,#ff0000)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#FF0000', endColorstr='#FF0000');
  !important}


#nav
{ background: url(http://i1080.photobucket.com/albums/j338/mishkalovers/TWIBANNER-1.png) center no-repeat !important; } }
