css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("www.foopets.com") {



#page, #content, #pet_info
{ background: transparent !important; }

body{background:#000000 url('http://i1080.photobucket.com/albums/j338/mishkalovers/havoc-1-3-1.jpg') fixed no-repeat 0 0!important}
	

a
{ color: #FFFF99 !important; }


h1, h2, h3

{ text-shadow: 1px 2px 2px #000 !important; 
color: #FFFF99 !important;
font-size: 32px !important; }


/* footer */
#nav_footer li
{ color: #FFFFFF !important;
font-size: 15px !important;
border: none !important; }


.foobutton
{ background: #000000 !important;
-moz-box-shadow: rgba(0,0,0, .8) 0px 4px 18px 2px !important; }

.foobutton:hover
{ border-color: #FFFF66 !important;
-moz-box-shadow: inset rgba(98,128,136,0.5) 0px 2px 8px 2px, rgba(98,128,136,0.5) 0px 2px 12px 2px !important;  }

.foobutton span
{ font-size: 15px !important;
color: #ffff66 !important;
text-shadow: 1px 2px 2px black, 1px 2px 2px black !important; }

#page
{ border-bottom: none !important; }

#subnav ul li{
    font-size:13px;
    height:28px;
    line-height:28px;
    padding:0;
    margin:0;
    border-right:1px solid #000000;
    border-left:1px solid #000000;
    background: #000000;/* old browsers */
    background: -moz-linear-gradient(top, #000000 0%, #000000 50%, #000000 50%, #000000 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(50%,#000000), color-stop(50%,#000000), color-stop(100%,#000000));  /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000000', endColorstr='#000000');
  !important}

#subnav ul li:hover{
    background: #00000;/* old browsers */
    background: -moz-linear-gradient(top, #00000 0%, #00000 50%, #00000 50%, #00000 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#00000), color-stop(50%,#00000), color-stop(50%,#00000), color-stop(100%,#00000)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#00000', endColorstr='#00000');
  !important}

  #subnav ul li.selected{
    background: #FFFFFF;/* old browsers */
    background: -moz-linear-gradient(top, #FFFFFF 0%, #FFFFFF 50%, #FFFFFF 50%, #FFFFFF 100%); 
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#000000), color-stop(50%,#000000), color-stop(50%,#000000), color-stop(100%,#FFFFFF)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000000', endColorstr='#000000');
  !important}


/* navbar logo */
#logo > A
{ width: 110px !important;
height: 90px !important;
margin-left: 60px !important;
margin-top: 2px !important;
 }


#nav
{ background: url(http://i1080.photobucket.com/albums/j338/mishkalovers/bannersunblast.jpg) center no-repeat !important; } }
