css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("evilmilk.com") {
html
{
  background: #000 !important;  /*-moz-linear-gradient(#1d123E, #000)*/
  background-repeat: repeat-x !important;
}

#main
{
  width: auto !important;
  position: relative !important;
  left: 10px !important;
}

#header .logo img
{
  width: 50px !important;
  height: 50px !important;
  position: absolute !important; 
  left: 5px !important;
  top: 2px !important; 
}

#header .logo
{
  width: 0px !important;
  height: 0px !important;
}

#header
{
  height: 0px !important; 
  width: 0px !important;

}

#header .menu
{
  position: absolute  !important; 
  left: 70px !important;
  top: -10px !important;
}

#middle
{
  width: 0px !important; 
  height: 0px !important;
}

#middle #main
{
  background: #000 !important;
  width: auto !important;
  position: absolute  !important; 
  left: 20px !important;
  top: 70px !important;
  height: 0px !important;
}

#middle #main .posted img 
{
  display: none !important;
}

 #sidebar , .pagination .archive , .pagination .button , .pagination .button2 , #footer , .menu , .menu a 
{
  display: none  !important;
}

.heading
{
  height: 50px !important;
  width:  584px !important;
  position: absolute !important;
  display: inline !important;
  top: -70px !important;
  left: 50px !important;
  background: -moz-linear-gradient( top ,#000,#000,#000, /*[[maincolor]]*/) !important;
/*background: #f00 !important;*/
}

.heading h1
{

  font-size: 15px !important;
}

.heading h1 img 
{
  display: none  !important;
  height: 0px !important;
  width: 0px !important;
}

.heading .headtext
{
  margin: 10px 0px 0px 50px !important;
  text-shadow: none !important;
  position: absolute !important;
  top: -5px !important;
  font-size: 15px !important;
  left: -40px !important;
  color: /*[[maincolor]]*/ !important; /*main page heading text*/
  width: 450px !important;
}

.heading h1 /*the heading text*/
{
  margin: 0px 0px 0px 0px !important;
  text-shadow: none !important;
  position: absolute !important;
  top: 12px !important;
  font-size: 15px !important;
  left: 10px !important;
  color: /*[[maincolor]]*/ !important;
  width: auto !important;
  height: auto !important;
  vertical-align: 0px !important;
  
}

/*descritpion */
.title a , .title a:visited { color:/*[[maincolor]]*/ !important; }
.title a:hover { color:#f00 !important; }
p.text { width:100px !important; color: #999 !important; }

.pagination /*the page 1-8 chooser*/
{
  position: fixed !important;
  width: 0px !important;
  position: absolute !important;
  top: 0px !important;
  left: -28px !important;
  background: #000 !important ; /*-moz-linear-gradient(left,  #666,  #000) !important*/
}

.pagination li
{
  width: auto !important;
  background: -moz-linear-gradient(left,  #666,  #000) !important;
  border-radius: 0px !important;
}
.pagination li a:hover
{
  background: -moz-linear-gradient(right,  #666,  #000) !important;
  border-radius: 0px !important;
}

.pagination li.text
{
  width: auto !important;
  background: -moz-linear-gradient(left,  /*[[maincolor]]*/,  #000) !important;
  border-radius: 0px !important;
}
.pagination li.text:hover
{
  background: -moz-linear-gradient(right,  /*[[maincolor]]*/,  #000) !important;
  border-radius: 0px !important;
}

.next , .prev , .home /*the navigation at each pic*/
{
  background-image: none !important;
  background: -moz-linear-gradient(top ,  #000, /*[[maincolor]]*/, #000 ) !important;
  color: #fff !important;
  text-align: center !important;
  font-size: 30px !important;
  vertical-align: bottom !important;
  text-decoration: none !important;
}
.home:after 
{ 
content:"H" !important;  

}
.prev:after 
{
content:"<" !important; 

}
.next:after 
{
content:">" !important; 
}

.post .thumb img 
{
  border-width: 0px !important ;  
  border-radius: 2px !important;
  -moz-transition: border-radius 199ms !important;
}

.post .thumb img:active
{
  /* delete this line if you do not want to use this feature mentioned above*/
  border-radius: 50px !important;
}

.info img.title
{
  display: none !important;
}
.info .rating
{
  color: /*[[maincolor]]*/ !important;
}
.share-photo  /*sharing*/
{
  position: relative !important;
  bottom: 50px !important;
  left: 60px  !important;
  background: #000 !important;
  color: #fff !important;
  font-size: 15px !important;
  text-align: center !important;

}
.share-photo a
{
  background: -moz-linear-gradient(bottom ,  #000, /*[[maincolor]]*/, #000 , #000 ) !important;
  color: #fff !important;
  text-decoration: none !important;
  font-weight:normal !important;
  left: -120px  !important;
  position: relative !important;
}
.share-photo a.email:after          { content:"@" !important; }
.share-photo a.thickbox.embed:after { content:"[code]" !important; }
.share-photo a.thickbox.url:after   { content:"URL" !important; }
.share-photo a.print:after          { content:"print" !important; }
.share-photo a.cell:after           { content:"cellphone" !important; }
.lightgrey , .break , .comments
{
  position: relative !important;
  bottom: 55px !important;
}
.photo-view div.break
{
  display: none !important
}
}
