    border: 1px solid rgba(0,0,0,.3) !important;
    box-shadow: 0px 0px 12px rgba(0, 0, 0, .1) !important;  
  }
  .uiSideNav a:hover {
    border: 1px solid rgba(0,0,0,.5) !important;
  }
  #navSearch * {
    padding: 0px !important;
    margin: 0px !important;
  }
