css:
@-moz-document domain(instapaper.com){

h2#categoryHeader a[href="/browse"],h2#categoryHeader span,#bookmarkListDeckAdPlaceholder,#header div[style="font-size: 14px; margin-top: 8px;"],a.textButton,a[title="Share..."],.tableViewCell span.separator,.autoPagerS,.autoPagerBorderPaging,#bookmarkListDeckAd,.tableViewCell a[title="Edit"]{display:none;}

#right_column,#userpanel,.pagination a[href="/edit"],#footer{opacity:0;}
#right_column:hover,#userpanel:hover,.pagination a[href="/edit"]:hover,#footer:hover{opacity:1}

body {background-color:white !important;width:auto !important;max-width:750px !important;padding:3%;overflow-x: hidden}
#right_column {margin-right:-200px;margin-top: 20px;}
#header {margin-bottom: 12px !important;}
#logo {text-transform:lowercase;font-style:italic;font-weight:normal !important;font-size: 20px !important;border-bottom: none !important;margin:0 auto 8px !important;}
#userpanel {font-size:12px !important;color:white;margin-top:8px !important}
#userpanel b {display:none}

#categoryHeader a[href="/archive"]:before {content:"/ "}
#categoryHeader a[href="/u"]:after {content:" /"}
#categoryHeader a {color:gray !important;}
#categoryHeader a:hover {text-decoration:none !important;}
#categoryHeader {font-weight:normal !important;font-size:20px !important;}
#paginationTop {margin: 6px 0 0 0 !important;}

#left_column, #bookmark_list {width:auto !important;max-width: 750px !important;}
.tableViewCell {background-color: white !important;padding:10px 0 !important;border-bottom:1px dotted #ccc !important;border-top:none !important;border-left:none !important;border-right:none !important;}
.tableViewCell .titleRow {width: auto !important;float:left !important;max-width:80%}
.tableViewCell .cornerControls {width:auto !important;}
.tableViewCell .secondaryControls span.host:first-child:after{content:"·";}
.tableViewCell .secondaryControls {float:right;clear:none;padding-top: 6px !important;padding-left:10px;}
.actionButton, .actionButton:visited, .actionButton:active {background-color: white !important;font-family:Helvetica,sans-serif !important;}

/* Credits */
#logo a[href="/u"]:hover:after {content:" = Marco + some CSS by @juanangeldr";font-size:12px;}

/* Options */
/*[[like]]*/

/* http://youtu.be/Sv6dMFF_yts */
}
