height: 6px !important;margin-top: 12px !important;

margin-left: 8px !important;}

#next_page_link {
color: #CDD2D9;
text-decoration: none;
background: url('http://i54.tinypic.com/29mknz6.jpg');
padding: 5px 8px;
margin-left: 10px;
white-space: nowrap;}

#return_to_top .return_to_top_icon{

background: url('http://i40.tinypic.com/1so0g5.jpg') no-repeat transparent!important}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://imageshack.us/a/img211/3664/nip.png")!important;background-repeat:no-repeat!important;width:23px!important;height:77px!important;margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;width:35px!important;height:35px!important}
.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}#inbox_button.tab.iconic.selected 
.selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important}

