@namespace url(http://www.w3.org/1999/xhtml);@-moz-document url-prefix('http://www.diandian.com/') {

/* messy coding by d-luffy.diandian.com
   feel free to customize any way you want!
    but i'd appreciate it if you'd keep the coding, font & image credits intact
     questions or issues? d-luffy.diandian.com/submit */


/* your background image is:
   a view of clouds in sky
          for more info visit discovertheweb.diandian.com  */

#content-holder ,.feed .link-to-post-holder ,.pb-action-shadow-top,.pb-action-shadow-bottom,.feed-ft-bottom,.feed-container-top ,.feed-container-bottom,.feed .editor-control-panel{ background:url('http://m3.img.srcdd.com/farm2/98/33046C022AE34F528E4AA341549C8B62_1366_768.JPEG') center right  repeat fixed !important; z-index:0; }




/* font-text good view shadow */


html, body, div,object, iframe,  p, blockquote, a, code, em, img, q, small, strong, dd, dl, dt, li, ol, ul, fieldset, form, label, table, tbody, tr, th, td, input ,.notes-comment,.radarpro-item-from{
    color:#CCC5BD ;
}

#aside .sidebox ,a.followed-tag.clearfix span ,.sidemenu li.sub a,.sidemenu li .sidemenu-item-extra ,.tag-nav li a, .tag-nav li a:link, .tag-nav li a:visited,#permalink-holder .permallink input,.jp-seek-bar.playing-mask-i ,.aside-editor-title ,.radarpro-item-name a{color:rgb(134, 0, 0) !important;}

.feed-bd .feed-title,.rich-content{color:#CCC5BD !important;text-shadow: 0px 1px 1px black, 0px 0px 2px #707070; }

.skin-button-willsilver-lite,#side-search-input.input-tip,.tag-content .tag-button,span.ui-button-text.skin-button-willsilver-lite-text,.notes-info{color:rgb(134, 0, 0) !important;}
.feed-comment .cmt-list .cmt-user,.feed-notes .feed-notes-list a,.notes-user,.feed-comment .cmt-list li .cmt-content a ,.feed-bd .feed-ct a{
color:#ff77ad  !important;

}






/* content trans gray color theme */

.feed .pop-content .feed-container-top .feed-container-bottom , .repost-feed {
 background: url('http://x.srcdd.com/farm1/9bd5eb/96f3d279/E023F.png') repeat scroll -1200px 0 transparent;
    border: 1px solid #D7D9DB;
    border-radius: 4px 4px 4px 4px;
    box-shadow: 0 0 1px #DEE0E2 inset;
    margin-bottom: 30px;
    overflow: hidden;
}


.pb-triangle,.pb-action{clear: both;
    background:url('http://x.srcdd.com/farm1/9bd5eb/96f3d279/E023F.png')  center left  repeat fixed !important; z-index:2; border-radius: 20px 10px 10px 20px; 
    overflow: hidden;
    word-wrap: break-word;
   }
.feed-user,.feed .pop-content ,.pop-triangle,.link-to-post-inner, .feed-notes,.feed-fold-footer,.feed-comment{
    clear: both;
    background:url('http://x.srcdd.com/farm1/9bd5eb/eb5f2700/9D9ED.png ') center right  repeat fixed !important; z-index:0; 
    border-radius: 20px 10px 10px 20px;
    font-weight: normal;
    line-height: 1.3em;
    margin: 3px 0 0;
    overflow: hidden;
    word-wrap: break-word;
}



/* Blue Tag cmt rt fav */
.feed-bd .feed-rt, .feed-bd .feed-cmt, .feed-bd .feed-del, .feed-bd .feed-edit, .feed-bd .feed-nt, .feed-bd .feed-block, .feed-bd .feed-report, .feed-bd .editor-promote-cancel, .feed-bd .editor-warning, .feed-bd .editor-black, .feed-bd .editor-black-top10, .feed-bd .editor-unblack-top10 {
height: 18px;
line-height: 18px;
border-bottom: 1px solid
#1E3B50;
background:
#144668;
background: -moz-linear-gradient(top,
#235B7B,
#144465);
background: -webkit-gradient(linear, 0 0, 0 bottom, from(
#235B7B), to(
#144465));
background: -o-linear-gradient(top,
#235B7B,
#144465);
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
-o-border-radius: 2px;
padding: 0 5px;
box-shadow: 0px 1px 2px
#CCC;
-o-box-shadow: 1px 1px 1px #CCC;
-webkit-box-shadow: 1px 1px 1px
#CCC;
-moz-box-shadow: 1px 1px 1px #CCC;
color:
white;}



/* it's like looking at everyone through a porthole */

ol#feed-list .feed-avatar{ border-bottom: 0px solid #0A2939; }

#main  .publisher .pb-avatar a.blog-avatar {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important; }
#main  #feed-list .feed .feed-avatar  a.blog-avatar {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important; }




}
