 domain("despora.de"),
 domain("londondiaspora.org"),
 domain("filiusdex.com"),
 domain("diasp.de"),
 domain("diasp.urbanabydos.ca"),
 domain("fused.at"),
 domain("diaspora.subsignal.org"),
 domain("diaspora.lt"),
 domain("efix.tk"),
 domain("diaspora.streusel.org"),
 domain("diasp.eu.com"),
 domain("diasp.fi"),
 domain("diaspora.dannielou.com"),
 domain("diaspora.xn--grne-lampe-beb.de"),
 domain("dpod.se"),
 domain("diaspora.isnap.gr"),
 domain("soc.ragriz.net"),
 domain("pod.chrisi01.de"),
 domain("foobar.cx"),
 domain("testy.kompisen.se"),
 domain("yaspora.es"),
 domain("diaspora.eigenlab.org"),
 domain("diaspora.sceal.ie"),
 domain("mariosabatino.info"),
 domain("diaspora.gpeni.net"),
 domain("rlb.co"),
 domain("www.geekspot.eu"),
 domain("diaspora.adlerweb.info"),
 domain("diasporapt.com"),
 domain("friends.gabewarren.com"),
 domain("pod.orkz.net"),
 domain("diaspora.chouchoune.fr"),
 domain("alt.md"),
 domain("lksnyder0.mooo.com"){

    * {
        outline: 0 !important;
    }

    body {
        background-color: #2C2C2C !important;
        font-family: "Liberation Sans" ,Helvetica,Arial,sans-serif !important;
    }
    
    h1, h2, h3, h4, h5, h6 {
        color: #999 !important;
    }
    
    a {
        color: #7E949E !important;
    }
    
    .stream_element, .stream_element .last_authored, .conversation.selected .last_author, .conversation.selected .timestamp, .note-content p, .note-content ul li, .note-content ul li strong{
        color: #9E9E9E !important;
    }
    
    .details, .timeago, .post_scope, .info, .via, .post_initial_info {
        color: #393939 !important;
    }
     
   
    .details p {
        color: #9E9E9E !important;
    }
    
    .from a{
        color: #566E78 !important;
    }
      
     .details a:hover{
        color: #393939 !important;
    }
     
    #publisher, .stream_container, .stream, .stream_element, .rightBar .title, #view_all_contacts_link, ul.show_comments, ul.comments li, ul.show_comments li, .likes_container li, .stream_element .reshare li, .stream_element .reshare, footer .container, .conversation_participants, #left_pane #left_pane_header {
        border-color: #333 !important;
    }
    
    .rightBar .section .content {
        color: #666 !important;
    }
    
    img {
        opacity: 0.5 !important;
    }
    
    img:hover {
        opacity: 1 !important;
    }
    
    ul.left_nav li.active > a:not(.sub_selected) {
        color: #666 !important;
    }
    
    ul.left_nav a.aspect_selector:hover, ul.left_nav a.home_selector:hover, ul.left_nav a.tag_selector:hover, ul.left_nav a.element_selector:hover {
        background-color: #444 !important;
    }

    .indicator, button {
        opacity: 0.5 !important;
    }
            
    textarea, #publisher_textarea_wrapper, input, select {
        background-color: #343434 !important;
        color: #9E9E9E !important;
        border-color: #444 !important;
    }
    
    header #global_search form input {
        box-shadow: 0 0 0 !important;
    }
    
    .button {
        opacity: 0.5 !important;
        border-color: #555 !important;
        box-shadow: 0 0 1px #999 !important;
        color: #222 !important;
    }
    
    hr {
        background: #333 !important;
    }
    
    header ul#user_menu.active {
        border-color: #777 !important;
    }
    
    header ul#user_menu li a:hover {
        color: #777 !important;
        background: #111 !important;
    }
    
    #facebox .content, .contact_list, .aspect_list {
        background-color: #222 !important;
        color: #666 !important;
        border-color: #666 !important;
    }
    
    .contact_list > .contacts > .contact, .aspect_list > .contacts > .contact  {
        background-color: #2C2C2C !important;
        border-color: #333  !important;
        box-shadow: 0 1px 5px #111 !important;
    }
    
    #aspect_controls {
        background-color: #333 !important;
        border-color: #444 !important;
    }
    
    ul.left_nav .contact_count {
        background: #444 !important;
        color: #747474 !important;
    }
    
    ul.left_nav li.active > a:not(.sub_selected) .contact_count {
        color: #999 !important;
    }
    
    .user_card {
        border-color: #333  !important;
        box-shadow: 0 1px 5px #222 !important;
    }
    
    label:not(.bootstrapped) {
        color: #484848 !important;
        text-shadow: 0 1px 1px #333 !important;
    }

    #hovercard {
        background-color: #2C2C2C !important;
        border-color: #333 !important;
    }
    
    #notification_dropdown, #notification_badge.active{
        background-color: #222 !important;
        border: 1px solid #222 !important;
    }
    
    #notification_dropdown .notification_element:hover {
        background-color: #111 !important;
    }

    #notification_dropdown .notification_element, #notification_dropdown .header {
        border-bottom: 1px solid #222 !important;
    }

    
    .hovercard_footer, #hovercard .hovercard_footer .footer_container a  {
        background-color: #333 !important;
        border-color: #555 !important;
        color: #666 !important;
    }
    
    .span-8 {
        color: #9E9E9E !important;
    }
    
    header ul#user_menu {      
        border-left: 0px !important;
        border-right: 0px !important;
    }
    
    header ul#user_menu.active {
        border: none !important;
    }

    .tag:hover{
        background: none !important;
    }
    
    .dropdown .wrapper {
	   background-color: #2C2C2C !important;
    }
    
    .dropdown .wrapper ul > li {
       color: #9E9E9E !important;
    } 
    
    .ltr p {
        color: #C1C1C1 !important;
    }
    
    .mentions, .mentions-input-box {
	   background-color: #343434 !important;
     }
    
    #profile {
        color: #C1C1C1 !important;
    }
    
    header .container a:hover{
        text-decoration: none !important;
    }
    
    .post-comment time {
        color: #666 !important;
        
    }
    
    .expander {
        color: #A9A9A9 !important;
        text-shadow: 0pt 0pt 7pt #111 !important;
        line-height: 55px !important;
        height: 37px;
        border-bottom: 2px solid rgb(44, 44, 44) !important;
        border-radius: 0px !important;
        background-image: -moz-linear-gradient(center top , rgba(44, 44, 44, 0) 0%, rgb(45, 45, 45) 95%) !important;
    }
    
    .comment{
        border-color: #525252 !important;
    }
    
    .conversation_participants {
        background-color: #2C2C2C !important;
    }
    
    ul.as-selections {
        border: 1px solid #444 !important;
        background-color: #343434 !important;
    }
    
    .stream_element.conversation:hover{
        background-color: #2C2C2C !important;
    }
    
    .stream_element.conversation.selected{
        background-color: #111 !important;
    }
  
 
    .stream_element.conversation .timestamp {
        color: #444 !important;
    }
    
     .conversation.selected .subject, .conversation.selected .timestamp {
        color: #444 !important;
    }   
        
    .post_preview {
        background-color: #222 !important;
    }
}
