css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('^(?!http://www\\.brchan\\.org/.*/src/)http://www\\.brchan\\.org/.*$'), url-prefix('http://www.ligadosaterra.org/kusaba/') {

/*Cleanup*/
hr,
div.adminbar,
.loading-visible,
table.pages input,
td.postblock,
td.rules span,
div span[style="float:right"],
div.thread span[style="float:right"],
div.postarea hr,
body center spam,
body center img,
div.replymode,
img.likebutton,
img.tweetbutton,
div.footer,
.logo+hr,
.logo+hr+div,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
.postarea>form>div[style]>a[target],
.doubledash {
display:none
}

/*General*/

* {
    border: none !important;
    background: none !important;
    font-size: 11px !important;
    font-family: Segoi UI !important;
    text-decoration: none !important;
    font-weight: normal !important;
}

a {
color: #a9c !important
}

/*Mascot*/
html {
    background: none /*Troque o link por uma imagem de seu gosto. Não passe de 200px de largura!*/ 
    fixed bottom 355px right no-repeat !important;
}

/*Post Box*/
table.postform {
    position: fixed;
    width: 260px !important;
    right: -4px; bottom: 3px;
    border-radius: 0px;
}

table.postform td {
padding: 0 !important
}

table.postform textarea {
    width: 250px;
    height: 100px;
    opacity: .9;
    -webkit-appearance: none;
    -moz-appearance: none;
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
    margin-left: 1px
}

table.postform textarea:focus {
    position: fixed;
    height: 300px;
    min-width: 500px;
    bottom: 53px; right: 1px;
    opacity: 1 !important;
    z-index: 99;
    border: 1px solid rgb(45,45,45) !important;
    margin-left: 1px
}

table.postform input {
    max-width: 180px; 
    opacity: .9;
    -moz-appearance: none;
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
    border-radius: 0px !important;
    margin-right: 2px;
}

input[type="submit"] {
margin-left: -3px;
margin-right: 0px !important;
width: 68px
}

input#spoiler {
    position: fixed;
    margin-top: 27px;
    margin-left: 63px
}

table.postform label {
position:fixed;
margin-top: 26px;
margin-left: 80px
}

input[type="password"], input[name="em"], input[name="subject"], input[name="name"]{
margin-left: 1px
}

input[type="password"] {
max-width: 44px !important
}

select[name="embedtype"] {
    -moz-appearance: none;
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
    margin-left: -3px
}

input[name="embed"] {
max-width: 126px !important;
margin-left: 1px;
}

input[name="imagefile"] {
max-width: 126px !important
}

td a.rules {
display: none !important
}

/*Catalog*/
td.rules li [href]{
    width: 248px;
    text-align: center;
    position: fixed;
    top: 115px;
    right: 3px;
    background-color: rgba(20,20,20,.0) !important;
    border-radius: 0px;
    padding: 0px 0px 2px 0px
}

td.rules {
    position: fixed;
    right: -9001px
}

/*Watch Threads*/
div.watchedthreads {
    right:3px;
    min-width: 248px;
    position: fixed !important;
    background-color: rgb(10,10,10) !important;
    border-radius: 0px
    }

/*Boards*/
div.navbar {
    min-width: 100%;
    position: fixed;
    padding: 0px !important;
    text-align: center;
    top: -1px !important; left: 0px;
}


/*Delete & Report*/
table.userdelete {
    max-width: 260px !important;
    position: fixed;
    right: -248px;
    bottom: 259px;
    border-radius: 0px;
    padding-bottom: 4px;
    -moz-transition: all 1.5s ease;
}

table.userdelete:hover {
    max-width: 254px !important;
    position: fixed;
    right: -2px; bottom: 259px;
    border-radius: 0px;
    -moz-transition: all 0.5s ease;
}

table.userdelete input {
    -moz-appearance: none;
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
}

/*Ferramentas /bs/*/
form[action="http://www.brchan.org/paint.php"]{
    position: fixed;
    width: 240px;
    border-radius: 0px;
    right: -245px; bottom: 350px;
    padding: 2px 0px 2px 15px;
    -moz-transition: all 1.5s ease;
}

form[action="http://www.brchan.org/paint.php"]:hover {
    position: fixed;
    width: 240px;
    border-radius: 0px;
    right: -5px; bottom: 350px;
    padding: 2px 0px 2px 15px;
    -moz-transition: all 0.5s ease;
}

/*Páginas*/
table.pages td {
    width: 234px;
    position: fixed;
    top: 130px; right: 5px;
    text-align: center;
}

/*table.pages:hover {
    background-color: rgb(10,10,10) !important;
    position: fixed;
    text-align:center;
    bottom: 2px;
    right: 10px;
    border-radius: 0px;
    opacity:1
}*/

/*Logo & Descrição*/
div.logo {
    position: fixed;
    max-width: 248px;
    min-width: 250px;
    min-height: 135px;
    top: 18px; right: 3px;
    background-color: rgba(20,20,20,.0) !important;
    border-radius: 0px;
    color: #d3d3d3 !important;
}

div.logo img {
    margin: 0px !important;
    max-width: 246px;
    height: auto !important;
    border: 1px solid rgb(45,45,45) !important;
    }

body center {
    /*height: 20px;
    width: 290px;
    text-align: center !important;
    position: fixed;
    right: 10px; top: 151px;
    background-color: rgb(10,10,10) !important;
    border-radius: 0px;*/
    display: none
}

/*Sizing*/

span.extrabtns img {
    max-height: 11px;
    background-size: 11px !important;
    border-radius: 3px;
}
form div table, form table {
    width: 100%
}

/*Não sei o motivo, mas sem esse padding alguns posts ficam desalinhados*/
form div div {
padding: 1px;
}

td.reply {
padding: 0px !important
}

body, form {
    margin-right: 123px !important;
}

td.highlight img.thumb {
    padding-left: 3px !important;
    padding-bottom: 2px;
}

td.highlight span.filesize {
    padding-left: 23px
}

td.highlight label {
    padding-left: 3px
}

blockquote {
    margin-left: 20px !important;
    padding-bottom: 5px;
    margin-top: 5px
}



/*Icons*/
img.hidethread {
    background: url('data:image/gif;base64,R0lGODlhEAAQALMAAAAAAP////b1+Hxvn39yon5yon9zon5yoYJ2pI2CrP39/v///wAAAAAAAAAAAAAAACH5BAEAAAsALAAAAAAQABAAAARa8BhJyzz4ml0wIZs0GQWRVaFhnkWJvRJRbimtxVQ2qrsH/iOTyQXyeV4fSwi0YV4oqUOn+DGplExpM1U0IASBsFiRsJIOX/E4MWmZnweqaGIV2Uce0soWGkQAADs=') !important;
}

img.quickreply {
    background: url('data:image/gif;base64,') !important
}

img.expandthread {
    background: url('data:image/gif;base64,') !important
}

img.watchthread {
    background: url('data:image/gif;base64,') !important
}

img.unhidethread {
    background: url('data:image/gif;base64,') !important;
    height: 11px;
    background-size: 11px !important;
    border-radius: 0px
}

/*Misc*/
div#updater.reply.dialog {
    background-color: rgb(20,20,20) !important;
    position: fixed !important;
}

div#updater input {
    -moz-appearance: none;
    background-color: rgb(40,40,40) !important;
    border: 1px solid rgb(45,45,45) !important;
}


.reply {
    border-radius: 0px;
}

hr {
    border-bottom: 1px solid rgba(0,0,0,0.5) !important;
    border-top: 1px solid rgba(255,255,255,0.1) !important;
    width: 300px;
}

div.reflinkpreview {
    background: rgb(30,30,30) !important;
    border-radius: 0px;
}

a[href="mailto:sage"] {
    text-decoration: none !important;
}

a[href="mailto:sage"]:before {
    content: 'sage ';
}

td.reply input, div.thread input {
    -moz-appearance: none;
    background-color: rgb(40,40,40) !important;
    border: 1px solid rgb(45,45,45) !important;
}

/**//**//**//*Colors & Font Settings*//**//**//**/

/*default font*/
* {
    color: rgb(150,150,150) !important;
}

/*background*/
html {
    background-color: rgb(20,20,20) !important
}

/*post box*/
table.postform { 
    background-color: rgba(20, 20,20, .0) !important;
}

/*spoiler*/
span.spoiler {
    color: #000 !important;
    background: #000 !important;
}
span.spoiler:hover {
    color: #d3d3d3 !important;
}

/*replys*/
.reply {
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
}

td.highlight a {
    color: #a9c !important;
}

/*name*/
.postername,.commentpostername, .admin {
    color: #a9c !important;
}

/*trip*/
.postertrip {
    color: #644 !important;
}

/*posts*/
blockquote {
    color: #d3d3d3 !important;
}

/*greentext*/
.unkfunc {
    color: #979 !important;
}

/*boards*/
div.navbar {
    background-color: rgb(10,10,10) !important;
}

div.navbar a {
    color: #666
}
div.navbar a:hover {
    color: #a9c !important;
}

/*file*/
span.filesize a {
    color: rgb(150,150,150) !important
}

/*thread "title"*/
.thread {
}

/*links*/
blockquote a , td.rules a, div.thread a, span.reflink a {
    color: #a9c !important;
}

/*sage identifier*/
a[href="mailto:sage"]:before {
    color: #a9c;
}

/*páginas*/
table.pages td {
    color: rgb(150,150,150) !important;
    background-color: rgb(20,20,20) !important;
}

table.pages td a {
    color: #a9c !important;
}

/*delete & report*/
table.userdelete {
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
}

/*/bs/ tools*/
form[action="http://www.brchan.org/paint.php"]{
    background-color: rgb(30,30,30) !important;
     border: 1px solid rgb(45,45,45) !important;
}

form[action="http://www.brchan.org/paint.php"] input, form[action="http://www.brchan.org/paint.php"] select#applet {
    -moz-appearance: none;
    background-color: rgb(30,30,30) !important;
    border: 1px solid rgb(45,45,45) !important;
}

}
