css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.sports.ru/') {

    /*RELEASE VERSION 1.4*/

    /*Меняем цвет фона на привычный серый. Заменяем шрифт всего сайта*/
    body {
        background-color:#dddddd !important;
        font-family: Verdana, Tahoma, sans-serif !important;
    }
  
    /*Меняем цвет фона центральной части на менее яркий белый*/
    .pageLayout{
        background-color: RGB(250,250,250) !important;
    }
  
    /*Меняем цвет шапки на сероватый*/
    .header-main {
        background-color:#92928b !important;
    }
  
    /*Меняем цвет футера на темно-серый и округляем нижние углы*/
    .footerLayout {
        border-bottom-left-radius:10px;
        border-bottom-right-radius:10px;
	   -moz-border-radius-bottomleft:10px;
	   -moz-border-radius-bottomright:10px;
	   background-color:#484843 !important;
    }
  
    /*Округляем углы главной навигации, внутренней навигации, 
    дайджест слухов, галлерей, блока прогнозов, навигатора
    матчей (в статистике и видео)*/
    .gallery, .digest, .item, .m-item, .gray-block, .lang, .predictor-block, .match-navigate {
        border-radius:10px !important;
        -moz-border-radius:10px;
        border:0px solid black !important;        
    }
  
    /*Размер шрифта навигации*/
    .m-item{
        font-size:13px;
    }
  
    /*Двигаем кнопку "Отправить" под текстовым полем комментариев 
    на новостных ветках на привычное место справа под полем*/
    .comment-form input{
        margin-left:318px !important;
    }
    
    /*То же самое с кнопкой на форумах*/
    .forum-form input{
        margin-left:588px !important;
    }
  
    /*Округляем верхние края у шапки сайта, у tabs блока
    трибуны и у лучших материалов*/
    .active, .tab, .header-main, .best-materials {
        border-top-left-radius:10px;
        border-top-right-radius:10px;
	   -moz-border-radius-topleft:10px;
	   -moz-border-radius-topright:10px;
    }
  
    /*Округляем нижние углы у всплывающего меню навигации,
    tabs блока трибуны, результатов поиска и саб-меню на 
    страницах видов спорта*/
    .hover-menu, .sub-hover-menu, .search-results-block, .third-menu-box, .sub-hover-menu A{
        border-bottom-left-radius:10px;
	   border-bottom-right-radius:10px;
	   -moz-border-radius-bottomleft:10px;
	   -moz-border-radius-bottomright:10px;
    }
  
    /*Округляем правый верхний угол у дополнительного
    всплывающего меню навигации*/
    .sub-hover-menu, .sub-hover-menu A{
	   border-top-right-radius:10px;
	   -moz-border-radius-topright:10px;
    }

    /*Изменяем величину шрифта в полях комментариев и самих
    комментариев к статусам*/
    textarea, .comment-text{
        font-size:12px !important;
    }
    
    /*Делаем фон заголовков в меню привычным зеленым
    с привычным белым шрифтом*/
    .bordered{
        background-color:#00AA32 !important;
        border-top-left-radius:10px;
        border-top-right-radius:10px;
        border-bottom:2px solid #00902A !important;
	    -moz-border-radius-topleft:5px;
	    -moz-border-radius-topright:5px;
	    padding: 3px 0 2px 5px !important;
	    color:white !important;
	    font-family: Verdana, Tahoma, sans-serif !important;
	    font-size:14px !important;
	    text-transform:none !important;
    }
    
    /*Изменяем цвет ссылок в заголовках на привычный белый*/
    .titleH3>A{
        color:white !important;
    }
    
    /*Модифицируем заголовки тегов в новостных ветках*/
    .tit{
        font: bold 14px white !Important;
        font-family: Verdana, Tahoma, sans-serif !important;
        color:white !important;
    }
    /*Изменяем цвет ссылок в заголовках тегов на белый.*/
    .tit>A {
        color:white !important;
    }
    
    /*Изменяем цвет ссылки "Добавить в избранное" на
    привычный голубой (ссылка видна только у тех, кто
    установил скрипт User Menu от Ezheloko)*/
    .toFav{
        color:#1C9FF6 !important;
    }
    
    /*Сдвигаем ссылку "Добавить в избранное" немного ниже
    под заголовком (ссылка видна только у тех, кто
    установил скрипт User Menu от Ezheloko)*/
    .favLink{
        padding-top:10px !important;
    }    
    
    /*Меняем шрифт в заголовках*/
    .titleH1{
        font-family: Verdana, Tahoma, sans-serif !important;
    }
    
    /*Фонт заголовков статей менее жирный*/
     .contentPart>.titleH1{
        font-weight:500 !important;
     }
    
    /*Меняем цвет шрифта в основных заголовках разделов
    на привычный голубой*/
    .bordered>.titleH1, .title-page>.titleH1{
        color:#1C9FF6 !important;
    }
    
    /*Менем цвет ссылок в основных заголовках Трибуны
    на привычный черный*/
    .titleH1>a{
        color:black !important;
    }
    
    /*Меняем фон основных заголов Трибуны на белый*/
    .title-page.bordered{
        background-color:rgb(250,250,250) !important;
        border-bottom:0px solid #00902A !important;
    }
    
    /*Меняем цвет шрифта "Друзья онлайн" в профиле*/
    .bordered>.titleH3{
      color:white !important;
	    font-family: Verdana, Tahoma, sans-serif !important;
	    font-size:14px !important;
	    text-transform:none !important;  
	  padding-top:3px !important;
    }
    
    /*Меняем цвет "_ друзей онлайн" на более темный*/
    .gray-text{
        color:rgb(80,80,80) !important;
        padding-right:5px !important;
    }
    
    /*Приглушенные плюсы и минусы (благодарность пользователю 
    natala)*/
    /*.sign {
        background-image: url(http://comed.in/object/ix_VRMsD3eg711.png) !important;
    }*/
    
    /*Возвращаем черный цвет рейтингу пользователя
    на страничке профиля*/
    .user-rating {
        color:black !important;
    }
    
    /*Слегка уменьшаем шрифт в графе "Дата" в календарях 
    команд*/
    .stat-table .bordR{
        font-size:11px !important;
    }

    /*Немного опускаем комментарии в статусах*/
    .comments-line {
        margin-top: 10px !important;
    }

    /*Исправляем положение кнопки "Создать блог ->" */
    .create-blog-but {
        margin: 0 !important;
    }

    /*Исправляем положение блока "Подписка на новости"*/
    .social-icons-big {
        bottom: 0 !important;
        margin-right: 6px !important;
    }
 

}
