@-moz-document domain("google.com") {

/* Please change the margin-left value if you do not satisfy with the position of the main stream*/
@media (min-device-width:1400px) and (max-device-width:2560px){
.bR, .ez, .AZa, .hia{
margin-left: 29% !important;}
}

@media (min-device-width:800px) and (max-device-width:1024px){
.bR, .ez, .AZa, .hia {
margin-left: 5% !important;}
}

@media (min-device-width:1025px) and (max-device-width:1399px){
.bR, .ez, .AZa, .hia  {
margin-left: 15% !important;}
}

/* Background */
body.wIa{
background: url(/*[[bg]]*/)  !important;
