@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.com") {
body{
background:RGB(245,245,245)!important;
}
/*顶部*/
.t_hdbgimg,.c_hg, .c_hg_pm,#contentLeft{
background:RGB(235,235,235)!important;
}
.ContentLeft .exco_main .lnav_topItemLabel, .ContentLeft .ContentFolderQuickViewList .lnav_itemLnk span.FolderLabel{
color:RGB(20,20,20)!important;
font:14px/22px "Segoe WPC","Segoe UI",Helvetica,Arial,"Arial Unicode MS",Sans-Serif!important;
}
.t_hdt, h1, .t_hdt a, .t_hdt a:visited, .t_hdt a:hover, .t_hdt a:link{
color:RGB(22,22,22)!important;
}
/*右侧*/
.lnav_topItemLnk,#contentLeft,.t_sel{
background:RGB(245,245,245)!important;
color:RGB(22,22,22)!important;

}
.LeftNav LI.lnav_item, .LeftNavBasic LI{
border:0px solid RGB(245,245,245)!important;
}
ected .lnav_topItem,.ContentLeft .lnav_topItem{
border:0px solid RGB(245,245,245)!important;
}
.LeftNav LI.lnav_item A.lnav_itemLnk, .LeftNav LI.lnav_item DIV.lnav_itemLnk, .LeftNav LI.lnav_item A.lnav_newLnk, .LeftNav LI.lnav_item.NoHover A.lnav_itemLnk:hover, .LeftNav LI.lnav_item.NoHover DIV.lnav_itemLnk:hover, .LeftNav LI.lnav_item #AddFldrInput, .LeftNavBasic LI A
{
border:0px solid RGB(245,245,245)!important;
color:RGB(22,22,22)!important;
}
.BorderBoxHover, .LeftNav LI.lnav_item .lnav_itemLnk:hover, .LeftNav LI.lnav_item .lnav_newLnk:hover, .LeftNav LI.lnav_item.MenuOpen .lnav_itemLnk, .ContentLeft .lnav_topItem:hover, .MenuOn.lnav_topItem, .LeftNavBasic LI A:hover
{
background:RGB(245,245,245)!important;
}
.Toolbar .ToolbarItem A, .ToolbarItem A{
color:RGB(20,20,20)!important;
font:14px/22px "Segoe WPC","Segoe UI",Helvetica,Arial,"Arial Unicode MS",Sans-Serif!important;
}
#NewMessage{
background:RGB(192,0,0)!important;
color:RGB(244,244,244)!important;
padding:2px 12px 2px 12px!important;
}
.TableHeaderGradient{
background:RGB(235,235,235)!important;
border:0px solid RGB(42,42,42)!important;
}
.MsgListToolbarsContainer .SortFilterContainer{
background:RGB(235,235,235)!important;
border:0px solid RGB(42,42,42)!important;
}
.FilterSelector .Fs_p, .FilterSelector A{
color:RGB(21,21,21)!important;
}
#messageListScrollableContainer,#messageListContentContainer{
background:RGB(235,235,235)!important;
border:0px solid RGB(42,42,42)!important;
}
.BorderNoTop{
border:0px solid RGB(42,42,42)!important;
}
.InboxTable TD{
border:0px solid RGB(42,42,42)!important;
color:RGB(21,21,21)!important;
}
.PrimaryTextColor, .ListItem SPAN, .ItemListContent A, .ListItem A, #MessageList A, .MessageListTitle, .MessageListTitle a, .AdvancedSearchTitle, .ToolbarItem SPAN.Disabled, .MeterText, #messageListContentContainer A, #fromAddrMenu A{
color:RGB(21,21,21)!important;
font:14px/22px "Segoe WPC","Segoe UI",Helvetica,Arial,"Arial Unicode MS",Sans-Serif!important;
}
.MessageListItems .ia_h{
background:RGB(245,245,245)!important;}

.BorderTop{
border:0px solid RGB(42,42,42)!important;
background:RGB(245,245,245)!important;
color:RGB(21,21,21)!important;
font:14px/22px "Segoe WPC","Segoe UI",Helvetica,Arial,"Arial Unicode MS",Sans-Serif!important;
}
}
