css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.taringa.net") {
body{
    background-position: center 50px !important;
}
#header{
    background-image: url('http://links.itaringa.net/links/bg-bar.gif') !important;
    height: 35px !important;
}
#logo{
    background-image: url('http://links.itaringa.net/links/logo-t.png') !important;
    height: 20px !important;
    width: 100px !important;
}
#nav{
    height: 30px !important;
}
#nav .floatR{
    display: none !important;
}
#nav ul li a{
    padding: 0px 12px !important;
    font-size: 12px !important;
    background: none !important;
}
#nav ul li a:hover{
    text-decoration: none !important;;
}
#menu-section-musica a{
    padding: 0px 12px !important;
    background: none !important;
}
#menu-section-mi a[href="/mi"]{
    padding: 0px 12px !important;
}
img[src="http://o1.t26.net/img/icon-new.png"]{
    display: none !important;
}
#menu-section-mi .bubble{
    display: none !important;
}
#menu{
    padding: 4px !important;
}
.floatL li.active{
    background-color: #18516c !important;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.1), 0 1px 0 rgba(0,0,0,0.2);
    border-radius: 15px;
    margin-top: 5px !important;
    padding: 1px !important;
}
#nav ul li.active a{
    padding: 0px 10px !important;
    margin-top: 0px !important;
    text-shadow: 0 1px 0 rgba(0,0,0,0.5);
    color: #fff !important;
}
.navbar{
    background: none !important;
    border: none !important;
    border-radius: 0 !important;
    top: 0px !important;
}
.navitem{
    background: url('http://links.itaringa.net/links/divider.png') no-repeat scroll right top transparent !important;
}
#header #search{
    margin-top: -10px !important;
    position: relative !important;
    right: 0px !important;
    top: 9px !important;
}
#header .searchq{
    background: rgba(0,76,133,0) !important;
    padding: 1px 7px !important;
    font-size: 11px !important;
    position: relative !important;
    z-index: 200 !important;
    color: #fff !important;
    text-shadow: 0 1px 0 #000 !important; 
    background: url('http://links.itaringa.net/links/divider.png') no-repeat scroll right top transparent !important;
    height: 35px !important;
}
#header .btn_search{
    display: none !important;
}
.tipsy{
    display: none !important;
}
.navitem.useritem ul{
    background-color: transparent !important;
    top: 5px !important;
    box-shadow: 0 0 5px 5px rgba(0,0,0,0.1) !important;
}
.navitem.useritem ul li:hover{
    background-color: #f7f7f7 !important;
}
.navitem.useritem ul li:last-child{
    border-radius: 0 0 3px 3px !important;
}
.navitem.useritem ul li:nth-of-type(1){
    border-radius: 3px 3px 0 0 !important;
    border-bottom: none !important;
}
.navitem.useritem ul li:nth-of-type(2){
    border-bottom: none !important;
}
.navitem.useritem ul li{
    background: none #fff !important;
    border-color: #ccc !important;
}
.navitem.useritem ul li a{
    color: #2c2c3e !important;
    text-align: left !important;
    font-size: 11px !important;
    text-shadow: 0 1px 0 #fff;
}
.active i.icon.home{
    background-position: -152px -119px !important;
}
i.icon.home{
    margin: 1px 0 0 !important;
}
.tabs li a{
    font-size: 12px !important;
    color: #7c7c7c !important;
}
.tabs .active a{
    background: #c5c5c5 !important;
    color: #fff !important;
    text-shadow: 0 1px 0 rgba(0,0,0,0.5) !important;
    box-shadow: inset 0 0 5px 0 rgba(0,0,0,0.2) , 0 1px 0px 0 #fff !important;
    padding: 0px 10px !important;
    margin-top: 2px !important;
}
.cont-filterH{
    background-image: url('http://k37.kn3.net/4D6749C2C.png') !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.4) !important;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.1) !important;
}
.cont-filterH ul li a.active{
    background-color: #909090 !important;
    background-image: none !important;
    box-shadow: inset 0 0 15px rgba(0, 0, 0, 0.3) !important;
}
.cont-filterH ul li a span{
    background-color: #696969 !important;
    box-shadow: inset 0 0 15px rgba(0, 0, 0, 0.5) !important;
    font-weight: bold !important;
    text-shadow: 0 1px 0 #000;
}
textarea{
    box-shadow: inset 0 0 5px 0 rgba(0,0,0,0.1) !important;
    border: 1px solid #bbb !important;
}
ul.my-shout-attach-options{
    margin-top: 0px !important;
}
.my-shout-attach-options .btn.g{
    margin-top: -13px !important;
    border-radius: 0 0 4px 4px !important;
    border-color: #bbb !important;
    margin-left: 0px !important;
}
.attach-video{
    margin-top: -13px !important;
    background-color: #fff !important;
    border: 1px solid #bbb !important;
    border-radius: 0px !important;
}
.attach-link{
    margin-top: -13px !important;
    background-color: #fff !important;
    border: 1px solid #bbb !important;
    border-radius: 0px !important;
}
.attach-image{
    margin-top: -13px !important;
    background-color: #fff !important;
    border: 1px solid #bbb !important;
    border-radius: 0px !important;
}
.my-shout-footer .my-shout-add{
    margin-top: -7px !important;
    border-top: 1px solid #bbb !important;
    border-radius: 4px !important;
    margin-right: 0px !important;
}
.my-shout-content-mi .my-shout-add{
    margin-top: -13px !important;
    border-top: 1px solid #bbb !important;
    border-radius: 0 0 4px 4px !important;
    margin-right: -5px !important;
}
.share-shout, .privacy-shout{
    margin-top: 0px !important;
}
.my-shout-textarea-mi textarea{
    border-radius: 5px 5px 0px 0 !important;
}
#notifications-navitem{
    background: url("http://links.itaringa.net/links/divider.png") no-repeat left center, url("http://links.itaringa.net/links/divider.png") no-repeat right center !important;
}
.char-count{
    z-index: 19 !important;
}
.bubble span{
    height: 6px !important;
    padding: 1px 6px 9px 0 !important;
}
.bubble a{
    height: 16px !important;
}
.bubble{
    bottom: 9px !important;
    right: 9px !important;
    border-radius: 5px !important;
    border-bottom: 1px solid #700c01 !important;
}
img[src="http://o1.t26.net/img/add.png"]{
    display: none !important;
}
}
