css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tvlistings.aol.com") {
	/* Brought to you by L34k3d */
	#header,
	.news,
	#logo,
	.listings-header-box.banner,
	#listings-ad,
	#below-listings-ad,
	div.relative,
	#guide-promo,
	#footer,
	.print {
		display: none;
		visibility: hidden;
		background-image: none !important;
		background-color: transparent;
	}
	#guideHeader {
	    background-image: none !important;
	    position: absolute !important;
	    text-align:center !important;
        top:0px !important;
        left:0px !important;
        right:0px !important;
        height:30px !important;
        width:100% !important;
        z-index: 910 !important;

	}
	#main-content, 
	#grid-timeslots,
	#grid-time,
	.timeHeader
	 {	   
		width: 100% !important;
	}

	body {
	    width: 100% !important;
	    background-image: none !important;
	    background-color: transparent !important;
		overflow-x: hidden !important;
	}

	a {
		font-size: 12px !important;
		font-family: "Comic Sans MS" !important;
		font-weight: normal !important;
	}

	#grid-sources {
		padding: 0px !important;
		font-size: 10px !important;
		text-align: center !important;
		width: 150px !important;
	}

	.grid-img,
	.grid-channel,
	.grid-network {
	   border:none;
	   text-align: center;
	   font-size: 10px !important;
	   padding: 0px !important;
	}
	
	#grid-flexible-header {
	   background-color: transparent !important;
	   position: fixed !important; 
	   top: 33px !important;
	   left: 0px !important;
	   right: 0px !important;
	   width: 100% !important;
	   z-index: 999999 !important;
}
	#grid-header,
	#grid-timeslot,
	#listings-grid
	{	
	    position:relative !important;
		z-index:10 !important;
		left: 0px !important;
		right: 0px !important;
		width: 100% !important;
	}
	#grid-source-scroller {
	position: relative !important;
	top:103px !important;
	left: 0px !important;
	right: 0px !important;
	width: 100% !important;
	z-index: -1 !important; 
	
	}

	.grid-time {
		text-align: center !important;
	}    
    
	#grid-tabs.grid-tabs {
	    background-image:url("http://tvlistings.aol.com/media/light-header-bg.gif") !important;
		position: fixed !important;
		left:0px !important;
		right:0px !important;
		top:-1px !important;
		overflow:hidden !important;
		width:100% !important;
        z-index: 100 !important;
	}
	.change, h2, h3 {
	     padding: 0px !important;
	     margin: 0px !important;
	}
	
	h3 {
    	 position: relative !important;
    	 top: -14px !important;
    	 left: 170px !important;
    	
	}
	.provider, .change{
    	 position: relative !important;
    	 top: -40px !important;
    	 left: 335px !important;
	}

	#listings-provider-info.listings-header-box {
	
	   padding:0px !important;
	   margin:0px !important;
	   text-align:center !important;
	   position: fixed !important;
	   left:0px !important;
	   right: 0px !important;
	   top:10px !important;
	   height: 30px !important;
       width: 100%!important;
       z-index: 1000 !important;
	}
	
}
