css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk"), domain("zadraw.ch", domain("dobrochan.ru") {
    body {
	   background: #f5f5f5 !important;
	   color: black !important;
	   font-family: Ubuntu, Trebuchet MS, sans-serif !important;
	   font-size: 14px;
	}
	a {
	   color: rgb(69, 102, 149) !important;
	   text-decoration: none !important;
	   font-weight: normal !important;
	}
	a:hover {
       color: rgb(40, 60, 100) !important;
    }
	blockquote {margin: 0.5em;}
	
	form {
	   width: 832px;
	   margin: 0 auto;
	}
	
	div.logo {
	   color: white !important;
	   width: 800px !important; height: 100px;
       padding: 0 16px; margin: 8px auto;
       background: #5a656a;
	   border-radius: 4px;
	   font-weight: normal;
	   font-size: 24px !important;
	   line-height: 2em;
	   text-align: left !important;
	   border-bottom: 1px solid white;
	}
	img.banner {
	   float: right;
	   opacity: 0.05;
	   margin: 0 !important;
	   margin-right: 32px !important;
	}
	img.banner:hover {
	   opacity: 1;
	   -moz-transition: opacity 0.5s linear 0s;
	}
	div.DESU_thread, div[id^="thread"] {
	   display: block;
	   width: 800px !important;
	   padding: 8px 16px;
	   margin: 8px auto;
	   background: white;
	   border-radius: 4px;
	}
	div.DESU_oppost {display: table-cell;}
	td.DESU_post, div[id^="replies"] {
	   clear: both; margin-left: 32px !important;
	   display: table !important;
	}
	
	div.DESU_oppost label {color: #ccc;}
	table.DESU_post label, div.DESU_post label {color: #aaa;}
	div.DESU_oppost label:hover,
	table.DESU_post label:hover, div.DESU_post label:hover {
	   color: black;
	   -moz-transition: color 0.3s linear 0s;
	}
	
	div.DESU_ytube {
	   display: block !important;
	   float: left;
	   opacity: 0.1;
	}
	div.DESU_ytube:hover {
	   opacity: 1;
	   -moz-transition: opacity 0.3s linear 0s;
	}
	td.reply, div.reply {
	   background: #eee !important;
	   color: black !important;
	   border-radius: 4px;
	   border: 1px solid #ddd !important;
	   padding: 4px !important;
	}
	td.reply {width: 754px !important;}
	
	div.reply[id^="DESU_hiddenthr_"], div#DESU_pform {
	   width: 780px !important;
   	   margin: 0 auto;
    }
    div#DESU_qarea {
        padding: 16px;
        border-radius: 4px;
    }
    div.reply[id^="DESU_hiddenthr_"] {opacity: 0.1;}
    div.reply[id^="DESU_hiddenthr_"]:hover {
	   opacity: 1;
	   -moz-transition: opacity 0.3s linear 0s;
    }
    div.reply[id^="DESU_hiddenthr_"] i {display: none;}
    
	div.reply[id^="DESU_preview_"] {
	   box-shadow: 0px 4px 4px rgba(0,0,0,0.1);  
	}
	
	img.thumb {
	   border-radius: 2px;
	   margin: 2px !important; margin-right: 16px !important;
	}
	
	span.spoiler {
	   background: #ddd !important;
	   color: #ddd !important;
	}
	span.spoiler:hover {
	   background: #ddd !important;
	   color: black !important;
	   -moz-transition: background 0.3s linear 0s;
	   -moz-transition: color 0.3s linear 0s;
	}
	span.filesize {font-size: 80%; line-height: 1;}
	
	hr, a.DESU_ytlink, span.thumbnailmsg,
	form>br, td.doubledash {display: none;}
	
	span.filetitle, span.replytitle, span.postername {
	   font-size: 14px !important;
       color: rgb(40, 60, 100) !important;
	}
	body>table, body>table td {border: none !important;}
	
	td.postblock {
	   background: transparent !important;
	   color: rgb(40, 60, 100) !important;
	   text-align: right;
	   font-size: 80%;
   }
   form#postform img {float: right;}
   div#DESU_qarea {
       background: #dbeaed !important;
       border-color: #c5d6e8 !important;
   }
   
   img.DESU_fullImg {
       border: none !important;
       border-radius: 4px;
       box-shadow: 0 2px 4px gray;
   }
}
