@-moz-document domain('http://203.177.253.218') {
	/* 
		the code in here only applies to:
			-pages on the domain http://203.177.253.218
	*/
body {
    background: none repeat scroll 0 0 #FFFFFF;
    margin: 0;
    padding: 0;
}
div.scroll_list {
    height: 400px;
    overflow: scroll;
    width: 140px;
}
div.text_input {
    font-family: sans-serif;
    font-size: 10px;
    overflow: auto;
}
.body_text {
    font-family: sans-serif;
    font-size: 13px;
}
.queue_text_red {
    color: red;
    font-family: sans-serif;
    font-size: 12px;
    font-weight: bold;
}
.queue_text {
    color: black;
    font-family: sans-serif;
    font-size: 12px;
    text-decoration: none;
}
.preview_text {
    background: none repeat scroll 0 0 #CCFFCC;
    font-family: sans-serif;
    font-size: 13px;
}
.preview_text_red {
    background: none repeat scroll 0 0 #FFCCCC;
    font-family: sans-serif;
    font-size: 13px;
}
.body_small {
    font-family: sans-serif;
    font-size: 11px;
}
.body_small_bold {
    font-family: sans-serif;
    font-size: 11px;
    font-weight: bold;
}
.body_tiny {
    font-family: sans-serif;
    font-size: 10px;
}
.log_text {
    font-family: monospace;
    font-size: 11px;
}
.log_text_red {
    background: none repeat scroll 0 0 #FF3333;
    font-family: monospace;
    font-size: 11px;
    font-weight: bold;
}
.log_title {
    font-family: monospace;
    font-size: 12px;
    font-weight: bold;
}
.sd_text {
    font-family: sans-serif;
    font-size: 16px;
    font-weight: bold;
}
.sh_text {
    font-family: sans-serif;
    font-size: 14px;
    font-weight: bold;
}
.sb_text {
    font-family: sans-serif;
    font-size: 12px;
}
.sk_text {
    font-family: sans-serif;
    font-size: 11px;
}
.skb_text {
    font-family: sans-serif;
    font-size: 13px;
    font-weight: bold;
}
.ON_conf {
    background: none repeat scroll 0 0 #FFFF99;
    color: black;
    font-family: monospace;
    font-size: 11px;
}
.OFF_conf {
    background: none repeat scroll 0 0 #FFCC77;
    color: black;
    font-family: monospace;
    font-size: 11px;
}
.cust_form {
    font-family: sans-serif;
    font-size: 20px !important;
    overflow: hidden;
}
.cust_form_text {
    font-family: sans-serif;
    font-size: 20px !important;
    overflow: auto;
}
