*{
margin    :0   !important;
padding   :0   !important;
font-size :1em !important;
}

div#simple-header,div.ad,div.sidebar,div#pager-top,div.day h2
{display:none !important;}

h1{background-image:none !important; font-size:1.4em !important; margin:0.5em 1em 0 !important;}
div.hatena-body{width:auto !important;}

div.main{margin:0.5em 1em !important; width:97% !important;}
div.main {border-left:2px solid #ffd9ec !important; border-right:2px solid #ffd9ec !important;}
div.section {padding:0 1em !important;}
div.section blockquote{margin:0.5em 0 !important; padding:0.5em 1em !important; font-size:0.9em !important; background-color:#e4ebf8 !important;}
.section p.sectionheader,.section h3.title{border:none !important;}
.section h3.title{background-color:#ffd9ec !important;padding:0.5em 1em !important;margin-left:-1em !important;margin-right:-1em !important;}
.section h3.title a:hover{color:#ff6bd1 !important;}
p.sectionheader{font-size:0.8em !important;}
div.section a[href^="http://komachi.yomiuri.co.jp/"]{font-size:0.8em !important;}
div.refererlist div.caption {text-align:left !important; margin:0.5em 1em 0 !important; font-size:0.9em !important;}
div.refererlist ul{margin-left:3em !important; font-size:0.8em !important;}
div#pager-bottom{border-bottom:2px solid #ffd9ec !important; font-size:0.8em !important; text-align:center !important;padding-top:2em !important;}
p.seemore{margin-bottom:1.5em !important;}
}
