css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://127.0.0.1:60080"), url-prefix("http://localhost:18481/"), domain("kingdomofloathing.com") {

body,
blockquote {
    background: #f2f5f6;
    background: -webkit-linear-gradient(left, #f2f5f6 0%, #ffffff 30%, #ffffff 70%, #f2f5f6 100%);
}
input.button:not([disabled]),
#menu p.rcm:hover,
.item, #pop_ircm,
div.guts, .header,
[style*="background-color: blue;"],
[style*="background-color:blue"],
table.stuffbox font[color="white"],
td[bgcolor="blue"],
td[bgcolor="#0000ff"],
td[style*="border"],
span.small > center > a[href="mchat.php"] b {
    color: #3c3c3c !important;
    text-shadow: 0px 1px 0px #f2f5f6;
    background: #f2f5f6; /* Old browsers */
    background: -moz-linear-gradient(top, #f2f5f6 0%, #b6b6b6 100%);    /* Firefox */
    background: -webkit-linear-gradient(top, #f2f5f6 0%, #b6b6b6 100%); /* Chrome */
}
input.button:disabled, input.button[disable] {
    color: #b3b6b6 !important;
    opacity: 0.9;
}

table.stuffbox font[color="white"] {
    color: #3c3c3c !important;
    text-shadow: 0px 1px 0px #f2f5f6;
}
#pop_ircm,
div#body,
#tabs li,
td[bgcolor="blue"],
td[style$="border: 1px solid #0000ff;"],
td[style$="border: 1px solid blue;"],
center#rollover + center > table[align="center"],
#nudgeblock,
input, .text,
#tabs a.tab {/*
    border-style: solid;
    border-width: 1px;*/
    border-color: #b3b6b6 !important;
}
#tabs li.active,
td[bgcolor="blue"][width="50"]:first-child {
    border-right-style: none !important;
}
td[align="right"][width="50"] {
    border-left-style: none !important;
}
#tabs.ui-sortable {margin-top: -2px !important;}
#tabs li.active {
    background: #fff !important;
}
#options #tabs > ul, 
input:hover,
input:focus,
#tabs a.tab:hover {
    border-color: #3c3c3c !important;
}
input,
input:hover,
input:focus,
#tabs a.tab {
    -moz-transition: 250ms border-color ease;
    -webkit-transition: 250ms border-color ease;
}
center > b > font[size="+2"] {
    text-shadow: 0 2px 2px #b6b6b6, 1px 1px 0px #b6b6b6;
}

span.collapsed:not(:hover),
font[size="+1"] > b > a[href="#top"]:not(:hover) {
    color: #b3b6b6 !important;
}
input.text, textarea,
#tabs a.tab.active,
.item:not(:hover) {
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 100%) !important;
    background: -webkit-linear-gradient(left, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 100%) !important;
}
.close {
    padding: 2px !important;
    border-radius: 10px;
    background-color: #fff;
    border: 1px solid #3c3c3c;
    opacity: 0.5;
}
.close:hover { opacity: 1;}

.inputform,
#tabs .tab:not([class="active"]),
.item, img.hand,
#nudgeblock,
[style*="border: 1px solid black;"] {
    border-radius: 2px !important;
}

#tabs a.tab.active {
    outline: none !important;
    border-bottom-width: 2px !important;
    box-shadow: 0 1px 1px black;
}
#tabs.colortabs .unread {
    background-color: #cff !important;
    box-shadow: 0 1px 2px turquoise;
}
#tabs a.tab.unread > .count {
    top: -5px !important;
    background-color: #3c3c3c !important;
    border-color: white !important;
    opacity: 0.75;
    font-size: 8px;
}

span.small > center > a[href="mchat.php"] b,
blockquote {
    margin: 5px;
    padding: 5px;
}
/* white background */
.chatdisplay, #guts {background: white;}

table[width="90%"]:only-child, /* familiar */
table[width="95%"] center:only-child > table:only-child,
.inputform,
blockquote {
    background: white;
    border-radius: 3px;
    border-color: #ccc;
    border: 1px solid #ccc;
}

/* hide WoL banner on home page */
a img[src="https://s3.amazonaws.com/images.kingdomofloathing.com/otherimages/kolwolbanner.png"],
/* hide standalone boxes */
span.collapsed,
table:only-child td[height="4"] {
    display: none;
}
table[width="95%"],
table[width="500"]:only-child,
table[width="350"]:only-child {
    box-shadow: 0px 2px 3px black, 0px 0px 5px gray;
}
/* end */

}

/* -- Colors --
 Silver:    #f2f5f6
 Dark gray: #b6b6b6
 Shadow:    #3c3c3c
 Highlight: #cff 
*/
