css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twinoid.com"), domain("www.hordes.fr") {
    
    /* MESSAGERIE TWINO */
    
    #frameContent, #myContacts  {
        background: #693e29 !important;
        color: #d19c73 !important;
    }
    #frameContent a {
        color: #f8f989 !important;
    }
    #frameContent a:hover {
        color: black !important;
    }
    #myContacts p, #frameContent .date {
        color: white !important;
    }
    .participants {
        background: #4a251d !important;
    }
    
    /* LIEN DE PROFIL SUR LE FORUM */
    
    
    #tid_userMenu .tid_rel, #tid_userMenu .tid_majorLinks a {
        background: #4a251d !important;
    }
    #tid_userMenu .tid_majorLinks a:hover {  /* Carrés */
        background: #9c0000 !important;
    }
    #tid_userMenu .tid_links a { /* Liens */
        color:white !important;
    }
    #tid_userMenu .tid_links a:hover {
        color:#f0ec56 !important;
    }
    
    /* BARRE TWINOID */
    
    #tid_menu .tid_user.tid_userNoBg.tid_userUnknown { /* Pseudo du joueur dans les notif */
        color: #eeef7d !important;
        font-size:11px !important;
    }
    .tid_login, .tid_bigTitle {
        color: #8a8085 !important;
        text-shadow: 0.1em 0.1em 0.2em black !important;
    }
    #tid_menu, #tid_userMenu div.tid_content { /* fond commun de tous les éléments de la barre Twino */
        background:  #693e29 !important;
        border: 1px solid #301813 !important;
    }
    .tid_read_true { /* Notif lue */
        opacity:0.5 !important;
        background: #5c3024 !important;
    }
    .tid_read_false, .tid_eventItem:hover, #tid_menu .tid_majors a, .tid_read_true:hover { /* Notif non lue ou survolée */
        background: #640000 !important; /* Fond rouge bordeaux */
    }
    #tid_menu a, .tid_title, .tid_site {   
        color: #ddab75 !important;
    }
    .tid_eventContent {
        color: white !important;
    }
    #tid_menu .tid_majors a:hover, #tid_menu .tid_actions a:hover {
        background: #9c0000 !important; /* Fond rouge vif pour le hover */
    }
    .tid_streamItem {
        color: white !important;
    }
    #tid_menu .tid_streamList a { /* Liens dans les news */
         color: #eeef7d !important;
    }
}
