css:
@-moz-document domain('news.ycombinator.com') {
body {margin:35px 0 0 !important;}
table {background:none !important;}

/* header */
table td[bgcolor] {margin:0 0 20px 0 !important;padding:5px !important;display:block !important; background-color:#eee !important; position:fixed !important; z-index:100 !important; top:0 !important; left:0 !important; width:100% !important; box-sizing:border-box !important;}
table td[bgcolor] b a {font-size:12px !important; width:99px !important; display:inline-block !important;}
table td[bgcolor] a {font-size: 16px !important;}
.pagetop b {margin-right:20px !important;}
table tr[style]:nth-child(2) {height:15px !important !important;}

tbody tr:nth-child(3) table {margin-left:0 !important; display:block !important;}
tbody tbody tr:nth-child(3n) {height:12px !important !important;}
tbody tr:nth-child(3n) table {width:100% !important;}

tbody {position:relative !important;}

/* hide numbering */
tbody tbody td.title:first-child {font-size:1px !important; color:#ccc !important; visibility:hidden !important;}

.title:nth-child(3) {position:relative !important; z-index:1 !important; padding:0 0 2px 0 !important;}
.title a {font-size:14px !important;}

.subtext {padding:2px 0 !important; color:#fff !important; position:relative !important;}
.subtext * {z-index:10 !important; color:#444 !important;}

/* points */
.subtext span {font-weight:bold !important; position:absolute !important; left:12px !important; top:1px !important; word-spacing:40px !important; white-space:nowrap !important; width: 50px !important; overflow:hidden !important; background:#fff !important; font-size:11px !important; z-index:0 !important;}

/* upvote */
a[id^=up] {display:none !important;}
table table tr:hover a[id^=up] {display:block !important;}

/* under title */
.subtext a {font-size:11px !important; color:#999 !important;}

/* url */
.comhead {position:absolute !important; top:21px !important; left:160px !important; font-size:10px !important; width:100px !important; text-overflow:ellipsis !important; overflow:hidden !important;}

/* username */
.subtext a:nth-child(2) {position:absolute !important;left:280px !important; color:#999 !important;}

/* flag */
.subtext a:nth-child(3) {position:absolute !important;right:0 !important; color:#000 !important;}
.subtext a:nth-child(3):hover {color:999 !important;}

/* comments */
.subtext a:nth-child(4) {position:absolute !important;left:60px !important;}
}
