css:
@-moz-document domain("www.tthfanfic.org") {
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    font-size: 13px !important;
    line-height: 18px !important;
    margin: 0 !important;
}

/* br + br + br {
display: none !important;
}
*/

p {
    padding: 0 !important;
    margin: 8px 0 !important;
}

.paragraphs {
    margin: 16px 0 !important;
    padding: 0 !important;
}

img { margin: 0 !important; padding: 0 !important; }

.formbody {
    margin: 0 8px !important;
    padding: 0 !important;
}

div[style="clear:both;"], div:empty {
    height: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    visibility: hidden !important;
}

/*------------------------------
Links
------------------------------*/
a {
    color: #08C !important;
    padding: 0 !important;
    display: inline !important;
}

a:hover {
    border: none !important;
    color: #005580 !important;
    text-decoration: underline !important;
}

a:focus {
    outline: none !important;
}

.defaultcolors {
    border: none !important;
    background: none !important;
}

/*------------------------------
Forms
------------------------------*/
textarea, select, input {
    font-size: inherit !important;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}

textarea {
    appearance: none !important;
    padding: 0 !important;
    border-radius: 4px !important;
    border: 1px solid #ccc !important;
}

/*------------------------------
Headers
------------------------------*/
h1, h2, h3, h4, h5, h6 {
    font-family: inherit !important;
    color: #333 !important;
}
h2 {
    background: none !important;
    border: none !important;
    font-size: 22px !important;
    line-height: 36px;
    margin: 0 8px 16px !important;
    padding: 0 0 16px 0 !important;
    border-bottom: 1px solid #ccc !important;
}

/*------------------------------
Tables
------------------------------*/
table {
    max-width: 100% !important;
    border-spacing: 0 !important;
    background-color: transparent !important;
    /* width: 100% !important */
    margin-bottom:18px !important;
    border: 1px solid #ddd !important;
    border-left: 0 !important;
    border-collapse: separate !important;
    border-radius: 4px !important;
}

table th,table td {
    background: none !important;
    line-height: 18px !important;
    text-align: left !important;
    vertical-align: top !important;
    border: none !important;
    border-top: 1px solid #ddd !important;
    border-left: 1px solid #ddd !important;
    padding: 8px !important;
}

table th {
    font-weight: 700 !important;
}

table thead th {
    vertical-align: bottom !important;
}

table tbody + tbody {
    border-top: 2px solid #ddd !important;
}

.table-condensed th,.table-condensed td {
    padding: 4px 5px !important;
}

table tr:first-child th:first-child,table tr:first-child td:first-child {
    border-radius: 4px 0 0 0 !important;
}

table tr:first-child th:last-child,table tr:first-child td:last-child {
    border-radius: 0 4px 0 0 !important;
}

table tr:last-child th:first-child,table tr:last-child td:first-child {
    border-radius: 0 0 0 4px !important;
}

table tr:last-child th:last-child,table tr:last-child td:last-child {
    border-radius: 0 0 4px 0 !important;
}

table tbody tr:nth-child(odd) td,table tbody tr:nth-child(odd) th {
    background-color: #f9f9f9 !important;
}
/*
table tbody tr:hover td,table tbody tr:hover th {
background-color: #f5f5f5 !important;
}
*/
table colgroup + thead tr:first-child th,table colgroup + thead tr:first-child td,table thead:first-child tr:first-child th,table thead:first-child tr:first-child td,table thead:first-child tr:first-child th,table tbody:first-child tr:first-child th,table tbody:first-child tr:first-child td {
    border-top: 0 !important;
}

/*------------------------------
Banner
------------------------------*/
.sitestyle {
    margin: 0 !important;
    background: #333 !important;
    padding: 4px 8px !important;
    border-bottom: 1px solid #222 !important;
}
.sitestyle, .sitestyle a, .sitestyle a:hover {
    color: #fff !important;
}

#sitebanner {
    margin: 8px !important;
    height: auto !important;
    border: none !important;
}

#sitebanner table {
    margin-bottom: 0 !important;
    border: 1px solid #ccc !important;
}

#sitebanner table td {
    text-align: center !important;
    vertical-align: middle !important;
    border: none !important;
    border-radius: 4px !important;
}
#sitebanner table td img {
    max-height: 100px;
}

#sitebanner td#bannerimage {
    text-align: center !important;
    padding-top: 14px !important;
    background: #444 url("http://static.tthf.me/images/titles/Landmarks.png") right top no-repeat !important;
}

/*------------------------------
Notifications
------------------------------*/
.announcement,
.admincolors,
.error,
h2 + div.formbody.defaultcolors p.paragraphs:only-of-type /* Maximum recommendations */
{
    border-width: 1px !important;
    border-radius: 4px;
    border-style: solid;
    font-weight: normal !important;
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5);
    padding: 8px 32px !important;
    margin: 8px !important;
    font-size: 13px !important;
    text-align: center;
}

.announcement p,
.admincolors p,
.error p
{
    margin: 0 !important;
    padding: 0 !important;
}

.announcement,
.admincolors
{
    background: #D9EDF7 !important;
    border-color: #BCE8F1 !important;
    color: #3A87AD !important;
}

.error,
h2 + div.formbody.defaultcolors p.paragraphs:only-of-type /* Maximum recommendations */
{
    background-color: #F2DEDE;
    border-color: #EED3D7;
    color: #B94A48;
}

.announcement a, .admincolors a {
    font-weight: normal !important;
}

/*------------------------------
Main Menu
------------------------------*/
#mainmenudiv {
    position: relative !important;
    background: linear-gradient(#333, #222) !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .25);
    border: 1px solid #000 !important;
    margin: -1px 0 16px -1px !important;
    z-index: 2000 !important;
    padding: 0 !important;
}

#mainmenu {
    padding: 0 3px !important;
}
#mainmenu li {
    padding: 0 !important;
}
#mainmenu li.bullet {
    display: none !important;
}

#mainmenu li a {
    display: block !important;
    padding: 14px 10px !important;
    color: #aaa !important;
    white-space: nowrap;
    margin: 0 !important;
}

#mainmenu li ul {
    position: absolute;
    z-index: 100 !important;
    background: #222 !important;
    top: 38px !important;
    border: 1px solid #000 !important;
    border-top: none !important;
    width: auto !important;
    padding: 0 !important;
    border-radius: 0 0 4px 4px;
}

#mainmenu li ul li a {
    padding: 8px 16px 8px 9px !important;
    min-width: 100px;
}

#mainmenu li a:hover {
    color: #fff !important;
    text-decoration: none !important;
}

/*------------------------------
Front Page
------------------------------*/
.categorylist, .structure {
    margin: 0 8px !important;
    width: auto !important;
}

/* .formbody + h2 {
border-top: 1px solid #ccc !important;
}
*/

/*------------------------------
Tabs
------------------------------*/
.tabsabove, tabsbelow {
    background: none !important;
    border: none !important;
}

.activetab, .touter, .tmid, .tinner {
    background: none !important;
}

.tabbartop, .tabbarbottom {
    margin: 0 8px !important;
}
.tabbartop {
    border-bottom: 1px solid #ccc !important;
    margin-bottom: 16px !important;
}
.tabbarbottom {
    border-top: 1px solid #ccc !important;
    margin-top: 16px !important;
}

.tinner {
    padding: 6px 12px !important;
    margin: 0 !important;
}

.inactivetab, .activetab {
    background: none;
    margin: 0 1px;
}

.inactivetab {
    padding-bottom: 1px !important;
}

.tabbartop .inactivetab, .tabbartop .activetab {
    border-radius: 4px 4px 0 0;
}
.tabbarbottom .inactivetab, .tabbarbottom .activetab {
    border-radius: 0 0 4px 4px;
}

a.inactivetab:hover {
    text-decoration: none !important;
    background: #eee;
}

.activetab {
    border: 1px solid #ccc;
}
.activetab .tinner {
    color: #555 !important;
}

.tabbartop .activetab {
    border-bottom: 1px solid #fff;
    margin-bottom: -1px;
}
.tabbarbottom .activetab {
    border-top: 1px solid #fff;
    margin-top: -1px;
}

/*------------------------------
All pages
------------------------------*/
.formbody {
    border: none !important;
}
.storynavigation {
    font-family: inherit !important;
    font-size: inherit !important;
    padding: 8px !important;
    border: none !important;
    border-bottom: 1px solid #ccc !important;
    background: none !important;
    margin: -16px 8px 16px !important;
}

.storynavigation #formatmenu li.bullet {
    display: none !important;
}
#formatmenu li {
    margin-left: 8px !important;
    margin-right: 8px !important;
}

.storynavigation label, .storynavigation select {
    margin: 0 8px !important;
}

.storynavigation *:first-child {
    margin-left: 0 !important;
}

.storynavigation ul#formatmenu li ul, ul.dropmenubody {
    background: #fff !important;
    padding: 4px 0 !important;
    border-radius: 4px;
    border: 1px solid #ccc !important;
    width: auto !important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    z-index: 1000 !important;
}

ul.dropmenubody { margin-top: 5px !important; }

.storynavigation ul#formatmenu li ul {
    top: 21px !important;
    left: 105px !important;
}

.storynavigation ul#formatmenu li ul li, ul.dropmenubody li {
    padding: 0 !important;
    margin: 0 !important;
}

.storynavigation ul li ul li a, ul.dropmenubody a {
    display: block !important;
    padding: 3px 16px !important;
    margin: 0 !important;
    white-space: nowrap;
    line-height: 18px !important;
}

.storynavigation ul li ul li a:hover, ul.dropmenubody a:hover {
    text-decoration: none !important;
    color: #ffffff !important;
    background-color: #0088cc;
}

.storylist {
    padding: 0 !important;
    margin: 0 !important;
    line-height: 18px !important;
}

.storylistitem {
    border: 1px solid #ccc !important;
    border-radius: 4px;
    padding: 8px;
    margin: 8px 0 !important;
}

/* On story list pages, story titles are .storytitle.titlecolors */

.titlecolors {
    background: #f9f9f9 !important;
    margin: -8px -8px 0;
    padding: 8px !important;
    border-bottom: 1px solid #ccc;
    border-radius: 4px 4px 0 0;
}

.titlecolors .storyicons {
    margin-top: -4px;
}

/*
.titlecolors .typeicon {
margin: 0 8px 8px 0 !important;
}
*/

.titlecolors .typeicon {
    display: none;
}

.storydesc {
    padding: 8px 0 !important;
    margin: 0 !important;
}

.storydetails {
    padding: 8px !important;
    border-top: 1px solid #ccc;
    margin: 0 -8px -8px !important;
}

.donoricon {
    display: none;
}

div.pages {
    margin: 16px auto 0 !important;
    max-width: 400px;
}
.pages td {
    font-family: inherit !important;
    text-align: right !important;
    vertical-align: middle !important;
    width: 25% !important;
}
.pages td + td {
    text-align: center !important;
    width: 50% !important;
}
.pages td + td + td {
    text-align: left !important;
    width: 25% !important;
}

.clearstyles {
    margin: 0 !important;
    padding: 0 !important;
}

.stats {
    background: none !important;
    margin: 16px 8px !important;
    padding: 16px 0 0 !important;
    border: none !important;
    border-top: 1px solid #ccc !important;
    text-align: center;
    font-family: inherit !important;
}

.copyright {
    padding: 16px !important;
    font-family: inherit !important;
    background: #333 !important;
    color: #fff !important;
}

/*------------------------------
Reviews
------------------------------*/
/* For Reviews on author, not story */
label[for=view] + form div {
    display: inline;
}

.storytitle + .reviewtitle {
    display: none;
}
.reviewtitle {
    font-size: inherit !important;
}
.reviewbody {
    margin: 8px 0 !important;
}

/*------------------------------
Profile
------------------------------*/
.avatar {
    border-radius: 4px;
}

img[src="http://www.tthfanfic.org/images/avatars/No-Avatar.gif"].avatar {
    display: none;
}

img.graph {
    margin: 0 0 16px 16px !important;
}

div[style^=text] img.graph {
    margin: 0 16px 16px 0 !important;
}

/*------------------------------
Review
------------------------------*/
form[action^=addreview] div {
    margin: 0 8px !important;
}

/*------------------------------
Story
------------------------------*/
select#chapnav {
    margin-left: 70px !important;
}

.storynavigation #formatmenu li form div[style^=display]:before {
    content: "Chapter: ";
    display: block;
    position: absolute;
    line-height: 21px;
}

/* Story download link */
li[style^="z-index"] > a[href^="/wholestory.php"]:first-child,
li[style^="z-index"] > a[href^="/wholestory.php"]:first-child:hover,
span.dropmenubutton
{
    background: #E6E6E6;
    border-radius: 4px;
    box-shadow: inset 1px 0 0 rgba(255, 255, 255, 0.125), inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05) !important;
    color: #333 !important;
    display: inline-block !important;
    text-shadow: rgba(255, 255, 255, 0.746094) 0 1px 1px;
    border: 1px solid #CCC !important;
    border-bottom-color: #B3B3B3;
    padding: 4px 8px !important;
    margin: -5px 0 -5px 105px !important;
    background: linear-gradient(#fff, #E6E6E6) !important;
    text-decoration: none !important;
}

span.dropmenubutton {
    padding: 0px 9px 0 2px !important;
    margin: -3px 0 -3px 10px !important;
}

li[style^="z-index"] > a[href^="/wholestory.php"]:after,
span.dropmenubutton:after /* Story list dropdown */
{
    content: " ";
    display: inline-block !important;
    border-left: 4px solid transparent;
    border-right: 4px solid transparent;
    border-top: 4px solid black;
    opacity: 0.3;
    height: 0;
    width: 0;
    position: relative;
    top: -3px;
    left: 3px;
}

span.dropmenubutton:after {
    top: -2px !important;
}

li[style^="z-index"] > a[href^="/wholestory.php"]:before {
    content: "Story Format: ";
    display: inline-block;
    position: absolute;
    left: 0;
}

.storysummary {
    padding: 8px 8px 0 !important;
    margin: 8px 8px 16px !important;
    border-radius: 4px;
    border: 1px solid #ccc !important;
}
.storysummary .typeicon {
    margin: 0 18px 8px 0 !important;
}
.storysummary table {
    margin-bottom: 8px !important;
}
.storysummary .nominations {
    margin: 0 0 4px 0 !important;
    padding: 0 !important;
}
.storysummary .nominations img {
    width: 300px;
    margin: 4px;
}

.storybody.defaultcolors {
    font: normal 14pt/20pt Baskerville;
    width: 30em;
    margin: 0 auto 16px !important;
}

#storyinnerbody.paragraphs {
    padding: 0 !important;
}

#storyinnerbody img {
    max-width: 100%;
    border-radius: 4px;
    /* border: 1px solid rgba(0, 0, 0, .55) !important; */
    box-shadow: 0px 3px 5px 0px rgba(0, 0, 0, .25);
    clear: both !important;
    float: none !important;
    display: block !important;
    margin: 16px auto !important;
}

a[accesskey=n], a[accesskey=n]:hover {
    display: inline-block !important;
    padding: 5px 14px !important;
    border: 1px solid #DDD !important;
    border-radius: 15px;
    text-decoration: none !important;
}

a[accesskey=n]:hover {
    background-color: #F5F5F5;
}

.defaultcolors {
    margin: 16px 8px !important;
}

div div.storylist.defaultcolors {
    margin: 0 !important;
    padding: 0 !important;
}
}
