css:
html body center {
    color: #f9f9f9;
}

html {
    background-color: #f5ead7;
}

td.title {
    color: #BBB;
    padding-right: 10px;
    line-height: 2em;
}

tr td center {
    padding-right: 10px;
}

td.title a {
    font-weight: bold;
}

td.subtext {
    line-height: 1em;
    color: #BBB;
    font-size: 10px;
}

.subtext span {
    font-weight: normal;
    color: #828282;
}

.subtext a:last-child {
    color: #ba7343;
}

span.pagetop {
    color: #DDDDDD;
}

span.pagetop a:last-child {
    font-weight: bold;
}

body center table {
    background-color: #F7F7F7;
    padding: 5px;
}

body > center > table {
    background-color: #F7F7F7;
    border-style: solid;
    border-width: 1px;
    border-color: rgba(186, 115, 67, 0.5);
    border-radius: 3px;
    box-shadow: 0px 0px 10px rgba(186, 115, 67, 0.3);
}

td.title a:hover {
    background-color: #f5ead7;
}

span.yclinks {
    color: DDDDDD;
}

span.yclinks a {
    color: #555555;
    padding-top: 1px;
    padding-bottom: 1px;
    padding-left: 3px;
    padding-right: 3px;
}

center form input {
    border-style: solid;
    border-width: 1px;
    border-color: #C1C1C1;
    line-height: 1.5em;
    border-radius: 2px;
    padding-left: 3px;
    padding-right: 3px;
}

span.pagetop a {
    color: #555555;
}

span.pagetop b a {
    color: #111111;
    padding: 2px;
}

span.pagetop b a:hover {
    background-color: #f5ead7;
    color: #000000;
}

span.yclinks a:hover {
    color: #333333;
    text-decoration: underline;
}

span.pagetop a:hover {
    color: #111111;
    background-color: #f5ead7;
}

td form textarea {
    border-color: BA7343;
}

span.comment {
    line-height: 1.5em;
}

td form input[type=submit] {
    background-color: #f5ead7;
    color: BA7343;
    border-color: BA7343;
    padding: 5px;
    cursor: pointer;
}

td form input[type=submit]:hover {
    background-color: #fadeaf;
}

span.comhead a:last-child {
    color: #B2B2B2;
}

span.comment a:last-child {
    color: BA7343;
    text-decoration: none;
}

span.comment a:last-child:hover {
    background-color: f5ead7;
}

td.subtext a:nth-child(3) {
    color: #b2b2b2;
}

table tr:nth-child(4) td {
    background-color: #f7f7f7;
}
