css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rutor.org"), domain("zerkalo-rutor.org"), domain("free-rutor.org") {

html body div#ws div#content center {display: none;}

div#all h1 {
    color: #000 !important;
    font-weight: bold !important;
    margin-left: 20px !important;
    margin-right: 280px !important;
    width: auto;
    text-shadow: #888 2px 2px 3px;
    text-indent: 10px;
    box-sizing: content-box;
    -moz-box-sizing: content-box;
    -webkit-box-sizing: content-box;
}
#menu_right_side {
    margin-right: 10px !important;
}

div#ws div#content {
    position: absolute;
    right: 10px !important;
    top: 200px !important;
}

div#ws div#sidebar {
    position: absolute;
    margin-right: 13px !important;
    right: 0 !important;
    width: 256px !important;
    top: 137px !important;
}
.sideblock {
    width: 250px !important;
    height: 56px !important;
    top: 0px !important;
}

a#fforum > img {
    width: 250px;
    height: 56px;
}

/*-- кнопка ФОРУМ. При желании, удалить этот блок правил, а класс добавить в список ниже --*/
.sideblock:nth-child(1){
    position: absolute;
    right: 260px !important;
}

/*[[forum]]*/ #mediaget_box, .sideblock:nth-child(4), .sideblock:nth-child(5), .sideblock2, .b_tz_on_top  {
    display: none !important;
}

div#index tr.tum td, div#index tr.gai td {
    font-family: Verdana, Aral !important;
    padding: 2px 0  !important;
}

tr.backgr td {
    width: 80px !important;
}

tr.backgr:hover {
background-color: #ffffff !important;
}


span {
    font-family: Verdana, Tahoma, Arial, sans-serif !important;
}
div#content > center > a > img {
    width: 24px;
    position: fixed;
    right: 5px;
    top: 10px;
    opacity: .3;
}

div#content > center > a > img:hover  {
    opacity: .7;
}
}


@-moz-document url-prefix("http://rutor.org/new"), url("http://rutor.org/top"), url("http://rutor.org/new#up"), url("http://rutor.org/top#up"), url-prefix("http://free-rutor.org/new"), url("http://free-rutor.org/top"), url("http://free-rutor.org/new#up"), url("http://free-rutor.org/top#up")  {
div#ws div#content {
             top: 140px !important;
}
}



@-moz-document url-prefix("http://rutor.org/torrent"), url-prefix("http://free-rutor.org/torrent") {
.sideblock:nth-child(1){
    display: none !important;
}
}
