css:
@namespace url(http://www.w3.org/1999/xhtml);

.global-nav, .global-nav .well, .btn-tweet, .btn-tweet:hover, .btn-tweet:active, .nav .active > a, .nav .open .dropdown-toggle, .form-search.has-saved-searches.focus, .tweet .actions {
background-image: url("http://dl.dropbox.com/u/6897924/twitterbg2.png")
}

.larry-topbar {
background-position: -120px 0;
width: 28px;
height: 20px;
margin-top: 10px;
}


i {  background-image: url("http://dl.dropbox.com/u/6897924/Twitter%20Skin/sprite%20ltr.png")
}


.fullname {
color: black;
}

.global-nav .search-input {
color: #CCC;
background-color: #FFF;
}

.nav li a {
color: white;
}

.dropdown-menu {
background-color: #2271A9;
border-color: #2271A9;
border-color: #2271A9;
}

.global-nav .highlight .search-input {
background-color: white;
}

.dashboard{
    float:right !important;
    margin-left:10px !important;
}
