css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {
/*
===== CSS Information ====================================

creat        2008/04/16 - maplebread
lastupdate   2008/05/26 - maplebread
Ver          1.3
URL          http://d.hatena.ne.jp/maplebread/

==========================================================
*/
*{
margin: 0 !important;
padding:0 !important;
font-weight:normal !important;
color      :#000   !important;
float      :none   !important;
}
body{
font-size        :0.8em     !important;
background-color :#eee      !important;
background-image :none      !important;
margin           :0.5em 1em !important;
height           :101%      !important;
}

/* [ja]ã‚¿ã‚¤ãƒ ãƒ©ã‚¤ãƒ³ã®ç”»åƒã‚’å‰Šé™¤ã™ã‚‹å ´åˆã¯ä»¥ä¸‹ã®ã‚³ãƒ¡ãƒ³ãƒˆã‚¢ã‚¦ãƒˆã‚’è§£é™¤ã—ã¦ãã ã•ã„ã€‚ */
/* [en]Timeline of the image removed commentout.                              ã€€  */
/* table#timeline img#profile-image {display:none !important;}                    */

/*åºƒå‘Šå‰Šé™¤*/
div#side_ad_base,
a[href^="http://ad.jp.doubleclick.net/"]{display:none !important;}

/*ãƒ˜ãƒƒãƒ€ç”»åƒ*/
h1#header a:after{content:"twitter /" !important;}
h1#header img    {display:none !important;}

/* ãƒ¬ã‚¤ã‚¢ã‚¦ãƒˆ */
#container,#content,#content #doingForm textarea,#content .doing,#content .doing .thumb,
#content .doing .user_actions,#side,#navigation,#user_search_q,#user_search_form,
#footer,#front #content,#front .wrapper,#front .intro,#front p a.join,#front #footer,
#signin,#signin input,#whatistwitter,#whatistwitter ul,#whatistwitter ul li,fieldset th,
fieldset input[type="text"], input[type="password"],#user_search_q,#bio-pics div,
.direct_messages .bar h3 select,.devices,#create_device input[type="text"],#create_device select,
.follow-button button,.follow-button input[type=submit], input[type=button].follow-button,
.remove-button button,.remove-button input[type=submit], input[type=button].remove-button,
.update-button button,.update-button input[type=submit], input[type=button].update-button,
button.small,button.med,div.big-btn,div.long-btn,div.med-btn,div.short-btn,input.big-btn,
#admin_side,.niceform label,.niceform input,.niceform label,div#videobutton,div#profilebox,
div#profiletext,div#profilebutton,input.profilesubmit,.side_thumb,#device_address,
.prototip .toolbar a.close,.prototip .default
{width:auto !important}
#container br {display:none !important;}

/*ã‚µã‚¤ãƒ‰*/
#side_base{width:100% !important;}
#side {background-color:#e0e0e0 !important;border:none !important; display:block !important;}
#side {padding-top:1em !important;padding-left:1em !important;}
#side .section-header {display:none !important;}
#side .section small  {display:none !important;}
#side ul       {width:15% !important; float:right !important; display:inline !important;}
#side ul.about {width:83% !important; float:left !important;}
#side ul.stats {width:15% !important; float:right !important; display:inline !important;}

#side div#friends        {clear:both !important;}
#side div#friends .vcard {display:none !important;}

#side ul.about li#bio .label {display:none !important;}
#side ul.about li     .label {width:4em !important;}

#side ul.stats li              {text-align:left !important;}
#side ul.stats li .label       {width:50px !important;}
#side ul.stats li .stats_count {text-align:right !important;}

#side .user_icon {display:none !important;}
#side #currently {display:none !important;}
#side .badgebox  {display:none !important;}
#side fieldset   {display:none !important;}

/*ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã‚»ãƒƒãƒˆ*/
fieldset .bar               {position:absolute !important; top:0.6em !important; left:7em !important;}
fieldset h3                 {float:left !important; font-size:1.4em !important; font-family:'Lucida Grande',sans-serif !important;}
fieldset #chars_left_notice {top:3em !important; left:-4em !important; font-size:1.4em !important; font-family:'Lucida Grande',sans-serif !important;}
fieldset .info              {position:absolute !important; top:4em !important; left:5em !important;}
fieldset center             {position:absolute !important; top:4em !important; left:32em !important;}

/*æœ€æ–°*/
h2.thumb     {font-size:1.8em !important; position:absolute; top:-0.2em !important;left:4em; padding:0 !important;font-family:'Lucida Grande',sans-serif !important;}
h2.thumb img {position:fixed !important; right:1em !important;}

/*ã‚¿ãƒ–*/
ul.tabMenu        {text-align:left !important; padding-left:55px !important; margin-top:1em !important; clear:both !important;}
ul.tabMenu a      {padding:0 1em !important;}
div.tab .badgebox {display:none !important;}
div.tab           {clear:both !important;}

/*ã‚¿ã‚¤ãƒ ãƒ©ã‚¤ãƒ³*/
#content div.desc                {padding-top:2em !important;}
#content div.desc p              {font-size:1.2em !important; padding:0.5em 1em !important;}
#content div.desc p.entry-title  {float:left  !important;}
#content div.desc p.entry-meta   {float:right !important;}

tr.hentry       {clear:both !important;}
tr.hentry:hover {background-color:#e0e0e0 !important;}
td.content               {border:none !important; width:140em !important; padding:0.5em 1em !important;}
td.content .entry-title  {float:left  !important;}
td.content .entry-meta   {float:right !important;}
td.content       a[href^="http://twitter.com/"]{display:none   !important;}
td.content .meta a[href^="http://twitter.com/"]{display:inline !important;}

div.bottom_nav .pagination       {text-align:right !important; padding:0.5em 1em !important;}
div.bottom_nav .left             {display:none !important;}
div.bottom_nav a                 {border:none !important;}
div.bottom_nav .statuses_options {display:none !important;}
div#footer                       {clear:both !important; padding-bottom:1em !important;}
div#footer a                     {padding:0.2em 0.5em !important;}

div#navigation      {position:relative !important; padding:1em !important; text-align:center !important; margin-top:-2em !important;}
div#navigation a    {padding:0.2em 0.5em !important;}
div#navigation form {display:none !important;}

tr.vcard              {float:left !important; width:33% !important; display:block !important;}
tr.vcard td.thumb img {margin:0 0.5em !important;}

}
