css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taringa.net")
{

.homes #main-col {
float: left !important
}

.post .post-title {
position: absolute !important;
z-index: 1 !important;
font-size: 16px !important;
border: 0 !important;
margin-left: 10px !important;
text-shadow: 0 1px 1px white !important;
margin-top: 10px !important;
}


.post .post-content {
display: block !important;
width: 705px !important;
overflow: hidden !important;
background: #E7E7E7 !important;
border: 1px solid #CCC !important;
position: relative !important;
margin-top: 7px !important;
border-top: 37px solid #CCC !important;
border-radius: 5px !important;
padding: 5px !important;
}

.Regular #main-col{float:left !important;border-right: 1px dashed #E5E5E5 !important;
}

form[name="newpost"] #main-col {
float: left !important;
}

#main-col {
float: right !important;
overflow: hidden !important;
width: 730px !important;
border-left: 1px dashed #E5E5E5 !important;
}

#post-author-box {
background-color: #E7E7E7 !important;
padding: 5px !important;
margin: 5px !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
background-image: url(http://i41.servimg.com/u/f41/16/71/63/22/fondod10.png) !important;
background-repeat: repeat-x !important;
box-shadow: 0 0 3px #939393 !important;
border: 1px solid white !important;
}

.box div.title h2 {
background:transparent !important;
}

.box div.title, .divider {
background:transparent !important;
}

.box .title .action {
float: right !important;
padding: 0 0 0 10px !important;
position: relative !important;
right: 0 !important;
z-index: 10 !important;
background: transparent !important;
}


.author-nick a{
color: #333 !important;
position: relative !important;
top: -2px !important;
}

#post-author-box .floatL.profile{
text-align: center !important;
width: 100% !important;
margin: 0 !important;
}

.big-avatar {
margin-right: 10px !important;
width: 120px !important;
height: 120px !important;
border: 2px solid white !important;
box-shadow: 0 0 3px black !important;
}


.author .rank {
color: #333 !important;
background-color: transparent !important;
border-bottom: 1px solid #CCC !important;
border-left:0 !important;
box-shadow: 0 1px 0 #fff !important;
}

.stats div span {
font-size: 11px !important;
color: #333 !important;
font-weight: bold !important;
}

#post-author-box .btn.v {
width: 200px !important;
text-align: center !important;
}

#post-author-box .btn.r {
width: 200px !important;
text-align: center !important;
}

#post-author-box .btn.g {
width: 200px !important;
text-align: center !important;
}

#user-metadata-post {
width: 228px !important;
}

#user-metadata-post div{
border-bottom: 1px solid #CCC !important;
box-shadow: 0 1px 0 #fff !important;
height: 30px !important;
}


#user-metadata-post div span{
float: left !important;
margin-right: 10px !important;
line-height: 30px !important;
}

.box .read-more span {
float: right !important;
padding: 3px 3px 3px 10px !important;
font-weight: bold !important;
font-size: 11px !important;
color: #777 !important;
background: white;
}

#main {
background:#fff !important;
}
}
