css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taringa.net")
{
#main.homes #main-col .box {
margin-left:-3px !important;
margint-right:-3px !important;
padding: 5px !important;
position: relative !important;
background-color: #E7E7E7 !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
background-image: url(http://i41.servimg.com/u/f41/16/71/63/22/fondod10.png) !important;
background-repeat: repeat-x !important;
box-shadow: 0 0 3px #939393 !important;
border: 1px solid white !important;
}

#main.homes #main-col .box .ui-button {
margin-top: 5px !important;
}

#main.homes #main-col .box .list{
margin-top: 5px !important;
}

#main.homes #main-col .box div.title h2 {
float: left !important;
padding-right: 10px !important;
margin: 0 !important;
font-weight: bold !important;
font-size: 13px!important;
background: transparent !important;
}

#main.homes #main-col .box div.title, .divider {
position: relative !important;
background: transparent !important;
}

#main.homes #main-col .box .title .action {
float: right !important;
position: relative !important;
right: 0 !important;
z-index: 10 !important;
background: transparent !important;
top: -5px !important;
}

.game-icon-big {
display: block !important;
float: left !important;
width: 50px !important;
margin-top:5px !important;
margin-right: 10px !important;
}

.game-icon-big img {
height:50px !important;
}

#main.homes #main-col .box .highlight-data {
float: left !important;
width: 228px !important;
}
}
