css:
/* Donne un aspect hordien à l'âme sur Hordes */
/* Copyright Nadawoo 2014 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hordes.fr"), domain("www.die2nite.com") {

    /* AME */

    .tid_bgRight, .tid_footerRight, .tid_userSheet .tid_userSheetBg .tid_bgRight { /* Bordure du passeport */
        border: 1px solid #91714D !important;
    }
    .tid_userSheet .tid_userSheetBg .tid_bgRight { /* Supprime la ligne haute du footer du passeport */
        border-bottom: none !important;
    }

    .tid_bgRight { /* Pied passeport */
        border-bottom: none !important;
    }
    .tid_footerRight { /* Pour le pied du profil Twino */
        border-top: none !important;
    }
    .tid_overall { /* Nombre de points */
        padding-top: 8px;
        margin-top: 0px !important;
    }
    .tid_bgRight, .tid_headerRight, .tid_userGoals_bgRight { /* Âme : supprime le coin bleu en haut à droite */
        background: none !important;
    } 
    .tid_bgRight, 
    .tid_footerRight, 
    .tid_userSheet .tid_userSheetBg .tid_bgRight {
        background: #5c3024 !important; /* Supprime le fond bleu du corps du passeport et de la tête/pied de l'âme */
    }
    .tid_details .tid_asv { /* Passeport : âge, etc. */
        padding-right: 5px;
    }
    .tid_headerRight, 
    .tid_goals, 
    .tid_goalListWrapper, 
    .tid_goalHelp {
        background-color: #3d2018 !important; /* Marron foncé */
    }
    
    
    .tid_userGoals .tid_goals .tid_top { /* Les 3 pictos mis en valeur en haut */
        margin-bottom: 0px !important; /* Supprime la bande marron dessous */
        padding-bottom: 13px !important;
   }
    
    .tid_goal { /* Chacun des 3 pictos mis en valeur */
        background: none !important;
    }
    
    .tid_userGoals .tid_tabs a.tid_active { /* Onglet actif */
        background:#7e4d2a !important;
        border: 1px solid #ffff6b !important;
        border-bottom: none !important;
    }

    
    /* Affichage des titres et des pictos sur la même page */
    
    .tid_block.tid_wins { /* Affiche les titres */
        display: block !important;
        margin: 0px 5px 0px 5px !important;
    }
    
    .tid_blockTitle, .tid_note, .tid_tabs { /* Masque les onglets */
        display: none !important;
    }


    /* Liste des titres */

    .tid_userGoals .tid_goals .tid_reachList .tid_scrollContent { /* Bloc contenant tous les titres */
       border-bottom: 1px dashed #A78675 !important;
       border-top: 1px dashed #A78675 !important;
       padding-top: 5px !important;
       padding-bottom: 10px !important;
    }
    
    .tid_userGoals .tid_goals .tid_reachList, .tid_goalListWrapper { /* Titres */
        overflow: visible !important;
        max-height: none !important;
        border: none !important;
        background: none !important; /* #5d3124 */
        box-shadow: none !important;
        
        background: url(http://data.hordes.fr/gfx/loc/fr/rewardsBg_header.gif) !important;/* Fond header mais avec haut masué. Evite l'effet de répétition de rewardsBg_bg.gif */ 
	    background-repeat: no-repeat !important; 
	    background-position: -8px -48px !important;
    }

    .tid_userGoals .tid_goals .tid_reachList table td.tid_name { /* Texte des titres */
         font-variant: small-caps !important;
         font-style: normal !important;
         font-weight: bold !important;
         color: white !important;
    }
    
    .tid_userGoals .tid_goals .tid_reachList table td.tid_value { /* Valeur Twino du titre */
        vertical-align: bottom !important;
    }
   .tid_userGoals .tid_goals .tid_reachList table td.tid_name img {
        margin-top: 10px !important; /* Espacement des groupes de distinctions */
    }
    
    
    /* Affichage des pictos */
    
    .tid_userGoals .tid_goals .tid_goalListWrapper { /* Bloc contenant tous les pictos */
        box-shadow: none !important;
        border: none !important;
        margin: 0px 4px !important;
        text-align: left !important;
        width: 265px !important;
        
        background: url(http://data.hordes.fr/gfx/loc/fr/rewardsBg_header.gif) !important;/* Fond header mais avec haut masué. Evite l'effet de répétition de rewardsBg_bg.gif */ 
	    background-repeat: no-repeat !important; 
	    background-position: -4px -50px !important;
    }
    
    .tid_userGoals .tid_goals .tid_mode_icons .tid_goal { /* Img picto + nombre */
        width: 31px !important;
        border: none !important;
    }
    
    .tid_userGoals .tid_goals .tid_mode_icons .tid_goal .tid_count { /* Nombre de pictos */
        margin-top: 1px !important;
        margin-left: -4px !important;
        text-align: right !important;
    }
    .tid_userGoals .tid_goals .tid_mode_icons .tid_goal img.tid_icon { /* Image du picto */
        margin-top: 10px !important;
    }
    
    .tid_userGoals .tid_goals .tid_mode_icons .tid_goal.tid_rare { /* Pictos rares */
		border: none !important;
		box-shadow: none !important;
		background-image: url(http://data.hordes.fr/gfx/icons/rewardBg_rare.gif) !important;
	}
    
    
    /* Fond marbré */
    
    #tid_1 .tid_userGoals { /* Bloc contenant tout le marbre */
        width: 258px !important;
		margin: auto !important;
		box-shadow: none !important; 
	}
		
    .tid_userGoals .tid_userGoals_headerLeft .tid_bgRight,
    .tid_userGoals .tid_userGoals_headerLeft .tid_bgRight,
    .tid_userGoals .tid_userGoals_headerBorder,
    .tid_userGoals_headerBottomLeft .tid_bgRight
     {
        background: none !important; /* Supprime les fonds */
        border: none !important;
    }
    
    .tid_userGoals {
		width: 260px !important; /* Largeur de l'ancienne âme */
	}
	
	
	.tid_userGoals .tid_userGoals_headerLeft .tid_bgRight { /* "Statistiques" */
	   background: url(http://data.hordes.fr/gfx/loc/fr/rewardsBg_header.gif) !important;
	   background-position: 0px -4px !important;
	   height: 13px !important;
	   padding-top: 50px !important;
	}
    	
    .tid_userGoals .tid_goals .tid_overall,
	.tid_userGoals .tid_goals .tid_top,
    .tid_userGoals .tid_goals .tid_mode_icons,
	.tid_userGoals .tid_goalHelp
	{ /* Corps du marbre (fond par défaut si pas de meilleur par-dessus) */
	   background: url(http://data.hordes.fr/gfx/design/rewardsBg_bg.gif) !important;
	}
	
    .tid_userGoals_footerLeft.tid_bg2 {/* "Que signifient ces compteurs ?" */
		height: 24px !important;
		background: url(http://data.hordes.fr/gfx/design/rewardsBg_footer.gif) !important;
		background-position: 0px -22px !important;
		background-repeat: no-repeat !important;
    }
     

   
   
   
   .tid_userGoals .tid_userGoals_footerLeft .tid_bgRight { /* Supprime le trait sous le bloc des pictos */
       display: none !important;    
    }
	.tid_userGoals .tid_goalHelp { /* Idem */
	   border: none !important;
	   margin-bottom: -1px !important;
	}
}
