css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
div.small_links A {
  position: absolute !important;
  top: 168px !important;
  font-size: 11px !important;
  z-index: 1001 !important;
  margin-left: -38px !important;
}
.add_and_remove {
  position: absolute !important;
  right: 410px !important;
}
#overflow_tags {
  display: inline-block !important;
  list-style: none !important;
}
#overflow_tags > LI > A > DIV {
  color: rgba(255,255,255,.65) !important;
  background-color: rgba(255,255,255,.05) !important;
  display: block !important;
  text-decoration: none !important;
  font-weight: 600 !important;
  padding: 10px 50px 10px 0px !important;
  width: 164px !important;
  text-indent: 38px !important;
  margin-left: -39px !important;
  border-right: solid thin rgba(0,0,0,.15) !important;
  border-bottom: solid thin rgba(0,0,0,.15) !important;
}
#overflow_tags LI A DIV:hover {
    background-color: rgba(255,255,255,.08) !important;
}
#overflow_tags > LI {
  position: relative !important;
  top: -46px !important;
}
#overflow_tags > LI > A {
  text-decoration: none !important;
}
#overflow_tags > LI:last-child {
  position: relative !important;
  margin-bottom: -45% !important;
}
#right_column > DIV:nth-child(4) > A > DIV {
  display: none !important;
}}
