css:
@-moz-document url-prefix("https://www.google.com/voice/") {
#gc-appbar {
   padding: 8px 0px 8px 0px !important;
   height: 32px !important;
}

td.goog-splitpane-first-container {
   width: 168px !important;
}

.gc-appbar-label-area.goog-inline-block {
   width: 130px !important;
   right: 25px !important;
}

/*Voice label*/
.gc-appbar-label {
   font-size: 18px !important;
   margin-left: 10px !important;
}

.goog-flat-menu-button {
   min-width: 25px !important;
}
.gc-appbar-buttons-left .goog-flat-menu-button {
   margin-right: 8px !important;
}

.jfk-button-standard {
   min-width: 45px !important;
}

.jfk-button-narrow {
  min-width: 30px !important;
}

.jfk-button-narrow.jfk-button-collapse-left {
  margin-right: 4px !important;
}

/* show All/Unread "Show:" label*/
.gc-appbar-show-label {
   display: none !important;
}

/* Unread Label*/
.gc-appbar-show-unread {
   margin-right: 5px !important;
   min-width: 45px !important;
}

.gc-appbar-message-area {
   min-width: 0px !important;
}

#gc-inbox-sidebar-main {
   margin-left: 20px !important;
   margin-right: 0px !important;
   padding-top: 8px !important;
}


/*Search Bar*/
#gbq2 {
  padding-left: 50px !important;
}

div.gb_gb {
  width: 700px !important;
}
}
