css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("livebeta.kaskus.us") {

/* Type 2 Branch */

/* Ad Removal */
/*---------------------------------------------> */
*[class^='ad'], *[id^='ad'], iframe, .author.vcard > i, .banner-top-ads {
display: none !important; }


/* Post Title in Forum View */
/*---------------------------------------------> */
/* Post Title */
.post-title {
-moz-transition: all 300ms ease 0ms !important;
width: 45% !important;
height: 18px !important;
overflow: hidden !important;
position: absolute !important; }

.post-title:hover {
width: 70% !important;
height: 18px !important;
padding: 3px !important;
margin-top: -3px !important;
margin-left: -3px !important;
border-radius: 4px !important;
background: rgba(255,255,255,.75) !important;
box-shadow: 0 0 20px 3px rgba(0,0,0,.3) !important;
z-index: 9999 !important;
position: absolute !important; }

/* Thread Nav */
.sub-meta {
margin-top: 23px !important;
position: relative !important; }

#searchform {
height: 0px !important;
margin-bottom: 0px !important; }

/* Forum Last Post */
#subforum-table .last-post > .author.vcard {
position: relative !important;
top: 18px; }

#subforum-table .last-post > .author.vcard:before {
content: 'by: ' !important; }

#subforum-table .last-post > .post-title {
width: 380px !important; }

#subforum-table .last-post > .post-title:hover {
width: 600px !important; }


/* Thread Data in Forum View */
/*---------------------------------------------> */
/* Rating */
.star-rate + span {
font-size: 80% !important; }

.star-rate {
margin: 0 auto !important; }

/* Jump Icon */
.jump {
margin: 2px 4px 2px 0 !important; }

/* Thread Row */
table#forum-home-table tbody td {
padding: 10px 2px !important; }

table#forum-home-table tbody tr {
-moz-transition: all 500ms ease 0ms !important;
border-bottom: 1px solid #E9E9E9 !important;
height: 41px !important; }

table#forum-home-table tbody tr:hover {
background: -moz-linear-gradient(center top , #EEEEEE 0%, #E2E2E2 100%) repeat scroll 0 0 transparent !important; }


/* Footer Nav */
/*---------------------------------------------> */
#nav-wrapper {
position: absolute !important;
top: 103px !important;
height: 30px;
z-index: 999999 !important; }

#hot-nav-wrap {
background: transparent !important;
height: 39px !important; }

.close-tab > .close {
display: none !important; }

.m-panel {
position: absolute !important;
top: 33px !important;
z-index: 999999999 !important;
background: rgba(255,255,255,0.6) !important;
height: 145px !important;
border: 2px solid rgba(0,0,0,.4) !important;
border-radius: 4px !important; }

#searchform > input[type='text'] {
position: relative !important;
width: 350px !important;
bottom: 80px !important;
left: 400px !important; }

#searchform > select {
position: relative !important;
width: 360px !important;
bottom: 50px !important;
left: 37px !important; }

#searchform > input[type='submit'] {
position: relative !important;
bottom: 109px !important;
left: 763px !important; }

/* Post Content */
/*---------------------------------------------> */
/* Spoiler & Quote */
.spoiler > #bbcode_spoiler_content, .post-quote > div:nth-child(2), pre {
background: rgba(0,0,0,.1) !important;
border-radius: 3px !important;
border: 1px solid rgba(0,0,0,.1) !important; }

.spoiler > #bbcode_spoiler_content > #bbcode_inside_spoiler {
background: transparent !important; }

/* Avatar */
.photo {
border-radius: 2px !important; }

/* User Info */
.user-info {
-moz-transition: all 500ms ease 0ms !important; }

#wrapper, #content-wrapper, #header-wrapper, #forum-home-table {
width: 100% !important; }

#main-search-result  {
position: absolute !important;
left: 12%;
margin-left: 220px !important; }

#sidebar-search-result {
position: absolute !important;
left: 12%; }

#content-body, #subforum-content, #post-content {
width: 95% !important;
margin: 0 auto !important; }

#left-column, #main-column, #right-column {
position: relative !important;
left: 12.5% !important; }

.modalBoxBodyContent > #main-column {
left: 0% !important; }

.user-content table tbody tr {
height: 32px !important; }

pre {
font-family: "Courier", "Helvetica", sans-serif !important; }

}



/* Whitelists */

@-moz-document url("http://livebeta.kaskus.us/user/feedback") {

iframe {
display: block !important; }

}


@-moz-document domain("livebeta.kaskus.us") {

*[class^='add'], *[id^='add'] {
display: block !important; }

}
