@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* To use with Movable Firefox Button extension */

#main-window[sizemode=normal][tabsontop] #toolbar-menubar[autohide="true"]{
margin-top:-17px;
-moz-padding-start:0px!important;
position: relative;
}
#main-window[sizemode=normal] #navigator-toolbox[tabsontop] > #toolbar-menubar[autohide] ~ #TabsToolbar {
-moz-padding-start:6px!important;
-moz-padding-end:110px!important;
}
/*hide the logo of Firefox Button
#appmenu-toolbar-button > .toolbarbutton-icon,
#appmenu-button > .button-box .button-icon {
display: none;
}*/

/*show the text of Firefox Button
#appmenu-toolbar-button > .toolbarbutton-text,
#appmenu-button > .button-box .button-text {
display: -moz-box !important;
}*/
/*hide the arrow of Firefox Button
#appmenu-toolbar-button > dropmarker,
#appmenu-button > .button-box dropmarker {
display: none;
}*/
#appmenu-button {
        border-radius: 4px 4px 0px 0px !important;
        height: 20px !important;
        padding: 0 5px !important;
        margin-bottom:-1px !important;
        margin-top:0.5px !important;
}
